method.code:
						var boolInsertingType = false;
var strConfigType = "";
var strDisplayType = "";
//--pointers

function oType()
{
	return _swdoc.sc_typei;
}

function MF()
{
	return _swdoc.mainform;
}

//-- init form fields
function setup_form_fields(boolInsert)
{
	_swdoc.boolInsertingType = boolInsert;

	app._een(_swdoc.MF().tb_code,boolInsert); //-- enable code change when inserting
	app._een(_swdoc.MF().btn_new_status1,(!boolInsert));
	
	app._een(_swdoc.MF().btn_new_status,(!boolInsert));	
	app._een(_swdoc.MF().btn_new_reltype1,(!boolInsert));
	app._een(_swdoc.MF().btn_new_att1, (!boolInsert));
	
	_swdoc.MF().tabType.ShowTabItem(1,!boolInsert);
	_swdoc.MF().tabother.ShowTabItem(3,!boolInsert);
	app._etab(_swdoc.MF().tabType,0);

	if(_swdoc.oType().pk_config_type.indexOf('->')<0)
	{
		//_een(_swdoc.MF().lbl_fk_config_type, false);
		//_een(_swdoc.MF().flg_inherit_reqtypes, false);
		app._een(_swdoc.MF().flg_inherit_autodiary, false);
		app._een(_swdoc.MF().flg_inherit_protected, false);
		app._een(_swdoc.MF().flg_inherit_regex, false);
	}else
	{
		//_een(_swdoc.MF().lbl_fk_config_type, true);
	}

	_swdoc.strConfigType = _swdoc.oType().pk_config_type;
	_swdoc.strDisplayType = _swdoc.oType().type_display;

}

						OnDataSaved()
{
	//-- saved data no longer inserting - setup fields for update
	if (_swdoc.boolInsertingType) 
	{
		app.cmdb.copy_parenttype_sc_statuslevels(_swdoc.oType().pk_config_type, _swdoc.oType().fk_config_type);
		_swdoc.MF().sl_status.Refresh();
		app.cmdb.copy_parenttype_sc_relationtypes(_swdoc.oType().pk_config_type, _swdoc.oType().fk_config_type);		
		_swdoc.MF().sl_reltypes1.Refresh();
		
		//-- Encode category name before setting "Status Definitions" filter
		var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
		var strFilter = "sf=fk_config_type,citypes_flg_src1&swc=fk_config_type&kvs=" + strEncodedCategoryName + "&fct=" + strEncodedCategoryName;
		app._slf(_swdoc.MF().sl_status,strFilter);
		_swdoc.MF().sl_status.Refresh();
	}
	
	_swdoc.setup_form_fields(false);
}

						OnFormClosing()
{
	// TODO: Add your event handler code here

    var pDoc = app.g.get_me_document("SERVICE_CATEGORY",1);
    if(pDoc)
    {
    	var intCurSel = pDoc.mainform.sl_types.curSel;
    	pDoc.mainform.sl_types.Refresh();
    	pDoc.mainform.sl_types.SetRowSelected(intCurSel,true);
    }

	return true;
}

							btn_new_subtype_OnPressed()
{
	//-- add subtype - pass in default values based on  this parent reocrd
	
	var strURL = "flg_canmonitor=" + _swdoc.oType().flg_canmonitor + "&fk_config_type=" + escape(_swdoc.oType().pk_config_type) + "&extended_form=" + _swdoc.oType().extended_form + "&fk_profile_code=" + _swdoc.oType().fk_profile_code + "&www_image_path=" + escape(_swdoc.oType().www_image_path) + "&extended_table=" + _swdoc.oType().extended_table;
	app.OpenFormForAdd("service_types", "", strURL, true, function(){
		sl_subtypes.Refresh();
		sl_subtypes.SetRowSelected(0);
		sl_subtypes_OnItemSelected(0);
		
	});
}

							btn_view_subtype_OnPressed()
{
	sl_subtypes.EditRecord(sl_subtypes.curSel);
	app.g.sl_refresh(sl_subtypes,null,0);	
	
}

							OnFormLoaded()
{
	//-- set defaults
	var boolInsert = (_swdoc.oType().pk_config_type=="");
	if(boolInsert)
	{

		//-- set fields from args, update doc and reste modified flag
		app.g.popuplate_form_fromargs("sc_typei", _swdoc, true, true);
		
	}
	
	//-- Encode category name before setting "Status Definitions" filter
	var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
	var strFilter = "sf=fk_config_type,citypes_flg_src1&swc=fk_config_type&kvs=" + strEncodedCategoryName + "&fct=" + strEncodedCategoryName;
	app._slf(_swdoc.MF().sl_status,strFilter);
	_swdoc.MF().sl_status.Refresh();
	
	
	//-- hide / show tab items depending on mode
	_swdoc.setup_form_fields(boolInsert);
	
	app._eim(img_ci,sc_typei.www_image_path);

	
	
	
}

							sl_subtypes_OnItemSelected(nRow)
{
	var boolEnable = (sl_subtypes.rowCount() > 0);
	app._een(btn_view_subtype,boolEnable);
	app._een(btn_del_subtype,boolEnable);
	

}

							btn_new_status_OnPressed()
{
	var strURL = "&flg_sc=1";
	app.OpenFormForAdd("service_category_status_new", "", strURL, true, function(){
		app.g.sl_refresh(sl_status,null,0);
	});
	
}

							btn_view_status_OnPressed()
{
	sl_status.EditRecord(sl_status.curSel);	
	app.g.sl_refresh(sl_status,null,0);
	
}

							tb_code_OnValueChanged(strValue)
{
	//-- if not set set it
	if(_swdoc.oType().fk_config_type=="")
	{
		_swdoc.oType().pk_config_type = strValue;
	}
	else
	{
		_swdoc.oType().pk_config_type = _swdoc.oType().fk_config_type	+ "->" + strValue;
	}
	if(app.cmdb.service_type_exists(_swdoc.oType().pk_config_type))
	{
		//-- this ci id and type already exists warn user and cancel save
		MessageBox("The " + _swdoc.oType().pk_config_type + " type already exists in the CMDB, a duplicate cannot be created");
		app._eva(mainform.tb_code, _swdoc.strDisplayType);
		app._ete(mainform.tb_code, _swdoc.strDisplayType);
		_swdoc.oType().pk_config_type = _swdoc.strConfigType;
		_swdoc.oType().type_display = _swdoc.strDisplayType;
		return false;
	}else
	{
		_swdoc.strConfigType = _swdoc.oType().pk_config_type;
		_swdoc.strDisplayType = strValue;
	}
	
		_swdoc.UpdateFormFromData();
}

							sl_status_OnItemSelected(nRow)
{
	var boolEnable = (sl_status.rowCount() > 0);
	app._een(btn_view_status,boolEnable);
	app._een(btn_del_status,boolEnable);
	
	
}

							btn_del_subtype_OnPressed()
{
	//--
	//-- Get delete type and make sure it doesnt have any associated items that are activated
	var delTypeID = sl_subtypes.GetItemTextRaw(sl_subtypes.curSel, 'pk_config_type');
	var arrParams ={};arrParams.ck_config_type=delTypeID;
	var intRowCount = app.g.sqs_rowcountbycol("sc_folio",arrParams);	
	
	if(intRowCount>0)
	{
		MessageBox("There are service items that are still associated to this service category.\nThey need to be re-assigned before you can delete this category.")
		return false;
	}
	
	//--
	//-- if it has sub types cannot delete
	var arrParams ={};arrParams.fk_config_type=delTypeID;
	var intRowCount = app.g.sqs_rowcountbycol("sc_typei",arrParams);

	
	if(intRowCount>0)
	{
		MessageBox("There are sub categories associated to this service category.\nThey need to be deleted before you can delete this one.")
		return false;
	}	
	
	var strConfirm = "Are you sure you want to delete this service category?";
	if(app.g.sl_deldbrow(sl_subtypes,sl_subtypes.curSel,1,null,strConfirm))
	{
		//-- delete related data

		app.g.delete_tablebycol("ct_statusl","fk_config_type",delTypeID);
		app.g.delete_tablebycol("ct_reltypes","fk_config_type",delTypeID);
		app.g.delete_tablebycol("ct_profiles","fk_config_type",delTypeID);		
	}


}

							btn_del_status_OnPressed()
{
	var strConfirm = "Are you sure you want to delete the selected status level?";
	app.g.sl_deldbrow(sl_status,sl_status.curSel,0,null,strConfirm);

}

