							btn_view_att1_OnPressed()
{
	var strURL = "flg_sc=1&fk_config_type=" + _swdoc.oType().pk_config_type + "&extended_table=sc_folio";
	var CurRow = sl_atts1.curSel;
	var strKey = sl_atts1.GetItemTextRaw(CurRow, 'pk_att_id');
	if(strKey!="")
	{
		app.OpenFormForEdit("cmdb_ctrl_fields", strKey, strURL, true, function(){
			sl_atts1.Refresh();
			sl_atts1.SetRowSelected(CurRow);
			sl_atts1_OnItemSelected(CurRow);
		});
	}
}

							btn_del_att1_OnPressed()
{

	//-- delete
	var strConfirm = "Are you sure you want to delete this field entry?";
	if(app.g.sl_deldbrow(sl_atts1,sl_atts1.curSel,'pk_att_id',null,strConfirm))
	{
		sl_atts1_OnItemSelected(0);
	}
	

}

