							tabType_OnTabSelected(nTab)
{
	if(nTab==1)
	{
		//-- Encode category name before setting filter
		var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
		var strFilter = "_swc_fk_config_type=" + strEncodedCategoryName;
		app._slf(_swdoc.MF().sl_subtypes,strFilter);
		_swdoc.MF().sl_subtypes.Refresh();
	}
}

							tabother_OnTabSelected(nTab)
{
	var tStats = 0;
	var tType = 1;
	var tFieldMang = 2;
	
	if(nTab==tStats)
	{
		//-- Encode category name before setting filter
		var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
		var strFilter = "sf=fk_config_type,citypes_flg_src1&swc=fk_config_type&kvs=" + strEncodedCategoryName + "&fct=" + strEncodedCategoryName;
		app._slf(_swdoc.MF().sl_status,strFilter);
		_swdoc.MF().sl_status.Refresh();
	}
	else if (nTab==tType)
	{
		//-- Encode category name before setting filter
		var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
		var strFilter = "sf=fk_config_type,citypes_flg_src1&swc=fk_config_type&kvs=" + strEncodedCategoryName + "&fct=" + strEncodedCategoryName;
		app._slf(_swdoc.MF().sl_reltypes1,strFilter);
		_swdoc.MF().sl_reltypes1.Refresh();
	}
	else if (nTab==tFieldMang)
	{
		
		//-- Encode category name before setting filter
		var strEncodedCategoryName = app.pfu(_swdoc.oType().pk_config_type);
		var strFilter = "sf=fk_config_type,citypes_flg_src1&swc=fk_config_type&kvs=" + strEncodedCategoryName + "&fct=" + strEncodedCategoryName;;
		app._slf(_swdoc.MF().sl_atts1,strFilter);
		_swdoc.MF().sl_atts1.Refresh();
	}


