{

}

							sl_choices1_OnItemDoubleClicked(nRow, nCol)
{
	btn_view1_OnPressed();
	return false;


}

							sl_choices1_OnItemSelected(nRow)
{
	app._een(btn_up1,(nRow>0));
	app._een(btn_down1, ((nRow+1) < sl_choices1.rowCount()));
	app._een(btn_remove1,(nRow>-1));
	app._een(btn_view1,(nRow>-1));
	
}

							btn_add1_OnPressed()
{

	//-- add form record
	var intIndex = app.g.sl_getmaxvalue(sl_choices1,'cindex');
	var strURL = "fk_qid=" + _swdoc.oQ().pk_qid + "&cindex=" + ++intIndex;
	
	app.OpenFormForAdd("wssm_wiz_qc", "", strURL, true, function(x){
		if(_swdoc.oQ().pk_qid<1){
			var strPK = x.document.strKey;
			if(strPK > 0){
				var strCurrentKeys = app.g.sqllist_get_valuesdel(sl_choices1, 'PK_QCID', ",", "");
				if(strCurrentKeys=="")
					strCurrentKeys = strPK;
				else
					strCurrentKeys += "," + strPK;
				sl_choices1.storedQuery = "common.load_by_colname";
				app._slf(sl_choices1 , "swc=PK_QCID&kvs="+app.pfu(strCurrentKeys));
			}
	
		}
		_swdoc.MF().sl_choices1.Refresh();
		sl_choices1.SetRowSelected(0, true, false);
		
	});	
}

							btn_remove1_OnPressed()
{

	var strConfirm = "Are you sure you want to delete the selected choice?";
	var deletedSequence = sl_choices1.GetItemTextRaw(sl_choices1.curSel, "cindex");
	if(app.g.sl_deldbrow(sl_choices1,sl_choices1.curSel,0,null,strConfirm))
	{
		//-- loop through list and set seq for each to #
		var intQ=0;
		var nextSeq=0;
		for(var x=0; x < sl_choices1.rowCount();x++)
		{
			var thisSequence = sl_choices1.GetItemTextRaw(x, "cindex");
			if(thisSequence>deletedSequence)
			{
				intQ = sl_choices1.GetItemTextRaw(x, "pk_qcid");
				var strParams = "cid="+app.pfu(intQ);
				app.g.submitsqs("form/wiz_question/dec_choice_seq", strParams);
			}
		}
		_swdoc.MF().sl_choices1.Refresh();
		sl_choices1.SetRowSelected(0, true, false);
	}
}

							btn_view1_OnPressed()
{
		
		sl_choices1.EditRecord(sl_choices1.curSel);
		sl_choices1.Refresh();
		sl_choices1.SetRowSelected(0, true, false);

}

							btn_down1_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	
	var currRow = sl_choices1.curSel;
	var intDownID = sl_choices1.GetItemTextRaw(sl_choices1.curSel, 'pk_qcid');
	
	//-- get id to up
	var intUpID = sl_choices1.GetItemTextRaw(sl_choices1.curSel+1, 'pk_qcid');
	
	//-- update seq id for both 
	var strParams = "cid="+app.pfu(intUpID);
	if(app.g.submitsqs("form/wiz_question/dec_choice_seq", strParams))
	{
		var strParams = "cid="+app.pfu(intDownID);
		if(app.g.submitsqs("form/wiz_question/inc_choice_seq", strParams))
		{
			//-- refresh list
			sl_choices1.Refresh();
			sl_choices1.SetRowSelected(currRow+1, true, false);
		}
	}
}

							btn_up1_OnPressed()
{
	if(!app.service.can_update(true))
		return false;
	//-- NWJ : move up 
	
	var currRow = sl_choices1.curSel;
	var intDownID = sl_choices1.GetItemTextRaw(sl_choices1.curSel-1, 'pk_qcid');
	
	//-- get id to up
	var intUpID = sl_choices1.GetItemTextRaw(sl_choices1.curSel, 'pk_qcid');
	
	//-- update seq id for both 
	var strParams = "cid="+app.pfu(intUpID);
	if(app.g.submitsqs("form/wiz_question/dec_choice_seq", strParams))
	{
		var strParams = "cid="+app.pfu(intDownID);
		if(app.g.submitsqs("form/wiz_question/inc_choice_seq", strParams))
		{
			//-- refresh list
			sl_choices1.Refresh();
			sl_choices1.SetRowSelected(currRow-1, true, false);
		}
	}
}

							OnFormLoaded(bDataReloaded)
{
	app.g.popuplate_form_fromargs("wssm_wiz_q", _swdoc, true, true)	
	
	app.g.dd_gettablelist(lb_table);
	app.g.dd_gettablelist(lb_table1);
	app.g.dd_gettablelist(fld_s_table1);
	
	if(_swdoc.oQ().dbtable!="")
	{
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_keycolumn);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_displaycolumn);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_keycolumn1);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_displaycolumn1);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, fld_extdet1);
	}
	if(_swdoc.oQ().sec_table!="")
	{
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().sec_table,false,false, fld_s_keycol1);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().sec_table,false,false, fld_s_txtcol1);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().sec_table,false,false, fld_s_detcol1);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().sec_table,false,false, fld_filtercol1);
	}

	if(_swdoc.oQ().pk_qid>0)
	{
		_swdoc.boolInserting = false;
		//-- hide show choices
		switch(app.LC(_swdoc.oQ().type))
		{
			case 'radiobox':
			case 'checkbox':
			case 'selectbox':
				TabQ.ShowTabItem(1,true);
				break;
			case 'option selector':
			case 'option selector - single select':
				TabQ.ShowTabItem(3,true);
				break;
			default:
				TabQ.ShowTabItem(1,false);
				break;
		}
		
		//-- hide show jump on tab item
		TabQ.ShowTabItem(2,_swdoc.oQ().flg_jumponanswer=="1");
	}
	app._etab(TabQ,0);
	_swdoc.init_next_lbs();


	app._een(lb_picker,(_swdoc.oQ().type=="Custom Picker"));
	app._ema(lb_picker, lb_picker.enable);
	if(_swdoc.oQ().targetcolumn=="opencall.probcode")
		app._een(fld_prefix, false);
		
	if (app.LC(wssm_wiz_q.type)!="radiobox" && app.LC(wssm_wiz_q.type)!="selectbox" && app.LC(wssm_wiz_q.type)!="option selector - single select")
	{
		//Disable clickbox "Jump to another question based on answer"
		app._een(flg_jump,false);
		
		//There is one exception in case is customer picker
		if (app.LC(wssm_wiz_q.type)=="custom picker"){
			if (app.LC(wssm_wiz_q.pickername)=="category" || app.LC(wssm_wiz_q.pickername)=="customer organisations" || app.LC(wssm_wiz_q.pickername)=="all organisations")
				app._een(flg_jump,true);
		}
	}

}

							lb_table_OnValueChanged(strValue)
{
	
	_swdoc.oQ().keycol="";
	_swdoc.oQ().txtcol="";
	if(_swdoc.oQ().dbtable!="")
	{
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_keycolumn);
		app.g.dd_getcolumndbnamelist(_swdoc.oQ().dbtable,false,false, lb_displaycolumn);
	}
	_swdoc.UpdateFormFromData();

}

							lb_type_OnValueChanged(strValue)
{
	app._een(lb_picker,(strValue=="Custom Picker"));
	app._ema(lb_picker, lb_picker.enable);

	wssm_wiz_q.pickername = (!lb_picker.enable)?"":wssm_wiz_q.pickername;
	_swdoc.UpdateFormFromData();
	
	
	 if (app.LC(strValue)!="radiobox" && app.LC(strValue)!="selectbox" && app.LC(strValue)!="option selector - single select"){
	 	app._eva(flg_jump,0);
		app._een(flg_jump,false);
		wssm_wiz_q.flg_jumponanswer = 0;
		
    	//Close tab jump to another question
    	TabQ.ShowTabItem(2,false);    
	 }
	 else {
      	  app._eva(flg_jump , wssm_wiz_q.flg_jumponanswer);
      	  app._een(flg_jump , true);
		
      	  if (flg_jump.value)
         	  TabQ.ShowTabItem(2,true);
		  }
	
	
	
	
	TabQ.ShowTabItem(3,false);
	//-- hide show choices
	switch(app.LC(strValue))
	{
		case 'radiobox':
		case 'checkbox':
		case 'selectbox':
			TabQ.ShowTabItem(1,true);
			TabQ.ShowTabItem(3,false);
			break;
		case 'option selector':
		case 'option selector - single select':
			TabQ.ShowTabItem(1,false);
			TabQ.ShowTabItem(3,true);
			break;
		default:
			TabQ.ShowTabItem(1,false);
			TabQ.ShowTabItem(3,false);
			break;
	}
	
	app._etab(TabQ,0);	
}

							flg_jump_OnValueChanged(strValue)
{

	TabQ.ShowTabItem(2,strValue=="1");
	app._etab(TabQ, 0);

}

							btn_view_check_OnPressed()
