		sl_checks.SetRowSelected(nRow, true, false);

}

							btn_remove_check_OnPressed()
{

	var strConfirm = "Are you sure you want to delete the selected value check?";
	if(app.g.sl_deldbrow(sl_checks,sl_checks.curSel,0,null,strConfirm))
	{
		_swdoc.MF().sl_checks.Refresh();
		sl_checks.SetRowSelected(0, true, false);
	}
}

							btn_add_check_OnPressed()
{
	//-- add form record
	var strURL = "fk_qid=" + _swdoc.oQ().pk_qid;
	app.OpenFormForAdd("wssm_wiz_qac", "", strURL, true, function(){
		_swdoc.MF().sl_checks.Refresh();
		sl_checks.SetRowSelected(0, true, false);
	});	
}

							sl_checks_OnItemSelected(nRow)
{
	app._een(btn_remove_check,(nRow>-1));
