							TabQ_OnTabSelected(nTab)
{
	// TODO: Add your event handler code here
	var tChoices = 1;
	var tJump = 2;
	
	if(nTab==tChoices)
	{
		_swdoc.MF().sl_choices1.Refresh();
	}
	else if(nTab==tJump)
	{
		_swdoc.MF().sl_checks.Refresh();
	}
	
}

							lb_picker_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	
	var type_introduced = app.LC(lb_type.value);
	var category_introduced = app.LC(strValue);
	
	
	if (type_introduced=="custom picker" && (category_introduced=="category" || category_introduced=="customer organisations" || category_introduced=="all organisations")){
	   app._eva(flg_jump , wssm_wiz_q.flg_jumponanswer);
       app._een(flg_jump , true);
		
       if (flg_jump.value)
           TabQ.ShowTabItem(2,true);
	}


	else {
		app._eva(flg_jump,0);
		app._een(flg_jump,false);
		wssm_wiz_q.flg_jumponanswer = 0;
		
    	//Close tab jump to another question
    	TabQ.ShowTabItem(2,false);  
	}

}
