function reset_form_fields()
{
	//-- clear search fields
	app.g.clear_form_fields("cmdb_stage",mainform,"",false);
	_ete(_swdoc.MF().flg_awatingonly, "1");
	_ete(_swdoc.MF().flg_delete, "0");
}
//--TK used to refresh fields in history
function history_refresh()
{
	//--TK If strStatus is empty then load the latest value from the mainform value
	if(!_swdoc.strStatus)_swdoc.strStatus = mainform.status;

	//-- Clear Main Form Value Ready for it to be Refreshed from the DB
	_ete(mainform.status , "");
	_ete(mainform.complete , "");

	//--Get Latest Process Details
	var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_stage_process");
	while(oRS.Fetch())
	{
		var strID = app.g.get_field(oRS, "pk_id");
		var strStatus = app.g.get_field(oRS, "status");
		var strComplete = app.g.get_field(oRS, "per_complete");
	}
	//--Set Main Form Value to that just taken from the database
	if(strStatus) _ete(mainform.status , strStatus);
	if(strComplete)	_ete(mainform.complete , strComplete+"%");

	if(strStatus == "Complete" && _swdoc.boolFromRefreshBtn)
	{
		MessageBox("Process Complete");	
		//-- Once Complete Kill Timer
		//_swdoc.timer = 0;
		//mainform.KillTimer(2);
	}
	_swdoc.boolFromRefreshBtn = false;
	//--If the Latest Value is Processing then Kick Off Timer only if _swdoc.timer not already running
	/*
	if((strStatus == "Processing" || strStatus == "Starting") && (_swdoc.timer == 0))
	{
		//--TK Set Timer Running
		//_swdoc.timer = 1;
		//mainform.SetTimer(2, 30000);

	}
	*/
	//--Refresh SL List
	//if(_swdoc.timer)
	//	_swdoc.MF().sl_history.Refresh();
}
//--
//-- process staged records
function process_staging_records(boolSelectedOnly)
{
	app.global.LogInfo("Process Records", "On Pressed", "Start", 1);
	_swdoc.arrUpdateProcess = new Array();
	_swdoc.arrDeleteRecords = new Array();
	var boolAwaitingOnly = (_swdoc.MF().flg_awatingonly.text=="1");
	var checkAddColVals = "";
	if(boolAwaitingOnly)
	{
		//-- we want to process those where import status = "awaiting or ""
		checkAddColVals = "import_status=awaiting";
	}
	//-- get list of identifiers we want to create

	var boolSuccess = true;

	if (_swdoc.currentList == undefined)
	{
		_swdoc.currentList = _swdoc.MF().sl_results;
	}

	var strCreateItemIDs = app.g.sl_get_valuesdel_andcheckcolvalue(_swdoc.currentList,"ck_config_item","import_action","create",checkAddColVals,"©","",boolSelectedOnly);
	if(strCreateItemIDs!="")
	{
		if(!_swdoc.process_create_records(strCreateItemIDs)) 
			boolSuccess = false;
	}

	if(boolSuccess)
	{
		//-- get list of identifiers we want to baseline and update
		var strUpdateItemIDs = app.g.sl_get_valuesdel_andcheckcolvalue(_swdoc.currentList,"ck_config_item","import_action","update",checkAddColVals,"©","",boolSelectedOnly);
		if(strUpdateItemIDs!="")
		{
			if(!_swdoc.process_update_records(strUpdateItemIDs))
				boolSuccess = false;
		}
	}

	//-- update cmdb stage records with status
	for(var i in _swdoc.arrUpdateProcess)
	{
		var p ={};
		p.stageditems = _swdoc.arrUpdateProcess[i];
		p.status = i;
		app.g.submitsqs("cmdb/process/stagedimport/update_import_status",p);
	}

	//-- Delete staging records
	for(var i in _swdoc.arrDeleteRecords)
	{
		var p ={};
		p.stageditems = _swdoc.arrDeleteRecords[i];
		p.table = i;
		app.g.submitsqs("cmdb/process/stagedimport/delete_extended_stageditems",p);
	}
	app.global.LogInfo("Process Records", "On Pressed", "End", 1);


	if(boolSuccess)	MessageBox("Processing Completed.")
}

//--
//-- baseline and update existing records
function process_update_records(strUpdateItemIDs, funcCallback)
{
	var arrUpdateItems = strUpdateItemIDs.split("©");
	var strItems = "";
	for(var x=0; x < arrUpdateItems.length;x++)
	{
		if(strItems!="")strItems+=",";
		strItems += arrUpdateItems[x];
	}	

	//-- get pk_auto_id from config_itemi
	var p={};
	p.stageditems = strItems;
	var oCountRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_affected_configitem_count",p,true);
	if(oCountRS.Fetch())
	{
		var intItemCount = oCountRS.GetValueAsString("CNT");
		if (intItemCount > 0)
		{
			var strDiaryDescription = "";
			//-- prompt for a diary update
			if(dd.GetGlobalParamAsNumber("Application Settings/CMDB/OnBaselinePromptDiary")==1)
			{

				strPlural = "s";
				if (intItemCount==1)
				{
					strPlural = "";
				}
				//-- Open form for analyst to add entry
				strPromptText = "Please enter diary text for the " + intItemCount + " CI" + strPlural + " to be updated:";
				strDiaryDescription = prompt(strPromptText,"");

			}
			var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_affected_configitems",p,true);
			while(oRS.Fetch())
			{
				var updItem = oRS.GetValueAsString("ck_config_item");
				var intID = oRS.GetValueAsString("pk_auto_id");
				var strType = oRS.GetValueAsString("ck_config_type");        	
				var intBLNID = oRS.GetValueAsString("baselineid");

				//set vars for ci relationship creation
				var intOrigSLD = oRS.GetValueAsString("fk_sld");
				var strOrigSite = oRS.GetValueAsString("fk_site");
				var strOrigCompany = oRS.GetValueAsString("fk_company_id");
				var strStageExtTable = "";
				//-- create new baseline
				var newCMDBID = app.cmdb.create_baseline_silent(intID, intBLNID,strType, true,strDiaryDescription);

				if(newCMDBID>0)
				{		
					var boolSite = false;
					var boolCompany = false;
					var boolSLA = false;
					var intNewSLD = 0;
					var strNewSite = "";
					var strNewCompany = "";

					//-- now update new CI with values from staging table
					//-- select fields from cmdb_stagefields
					var strUpdateCI = "";
					var strUpdateExt = "";
					var strExtTable = "";

					var p={};
					p.stageditem = updItem;
					var oRSFields = app.g.get_sqrecordset("cmdb/process/stagedimport/get_item_stagefields",p);

					while(oRSFields.Fetch())
					{
						//--
						var strNewValue = oRSFields.GetValueAsString("newvalue");
						var strColName  = oRSFields.GetValueAsString("targetcol");
						var strTableName  = oRSFields.GetValueAsString("targettable");

						if(strTableName=="config_itemi")
						{
							//set flags and values for ci relationship checks
							if(strColName=="fk_site")
							{
								var boolSite = true;
								var strNewSite = strNewValue;
							}
							if(strColName=="fk_company_id")
							{
								var boolCompany = true;
								var strNewCompany = strNewValue;
							}
							if(strColName=="fk_sld")
							{
								var boolSLA = true;
								var intNewSLD = strNewValue;
							}

							if(strUpdateCI!="")strUpdateCI+="&";
							strUpdateCI += "_swc_" + strColName + "=" + pfu(strNewValue);
						}
						else
						{
							strExtTable = strTableName;
							strStageExtTable = strExtTable+"_stage";
							if(strUpdateExt!="")strUpdateExt+="&";
							strUpdateExt += "_swc_" + strColName + "=" + pfu(strNewValue);
						}
					}

					//-- perform updates

					//-- config_itemi
					if(strUpdateCI!="")
					{

						var boolRes = app.g.submittableupdate("config_itemi",newCMDBID,strUpdateCI)	
					}

					//-- ext table
					if(strUpdateExt!="")
					{
						var boolRes = app.g.submittableupdate(strExtTable,newCMDBID,strUpdateExt);	
					}


					if(!boolRes)
					{
						MessageBox("Failed to update staging record (" + updItem + "). Please contact your Supportworks Administrator.");
					}
					else
					{
						//if updates successful, update CI relations
						var boolCheckOrg = true;

						//if site has changed
						if(boolSite)
						{
							//if site is not already loaded, load site
							var strSite = strOrigSite;
							recSite = false;
							if(_swdoc.arrSites[strSite]!=undefined)
							{
								var recSite = _swdoc.arrSites[strSite];
							}
							else
							{
								app.global.LogInfo("Process Import", "Get Site", strSite, 1);
								var recSite = app.g.get_record("site", strSite);
								_swdoc.arrSites[strSite] = recSite;
							}
							//clear current site relationship
							if(recSite)
							{
								app.cmdb.delete_meconfigrelation(recSite.fk_cmdb_id,newCMDBID);  
							}

							//if site is set
							if(strNewSite != "")    		
							{
								var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCIRelationship");
								var strSite = strNewSite;
								recSite = false;
								if(_swdoc.arrSites[strSite]!=undefined)
								{
									var recSite = _swdoc.arrSites[strSite];
								}
								else
								{
									app.global.LogInfo("Process Import", "Get Site", strSite, 1);
									var recSite = app.g.get_record("site", strSite);
									_swdoc.arrSites[strSite] = recSite;
								}

								if(recSite!=false)
								{
									app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,newCMDBID, strRel);
									// PM00131778 - Remove code that prevents the creation of Org relationship
									// boolCheckOrg = false;
								}
							}else
							{
								//site has been cleared
								boolCheckOrg = true;
							}
						}

						//will only be run if no site link
						if(boolCheckOrg)
						{
							//site has been set blank or unable to create site rel, create org rel to new company
							if(boolCompany)
							{
								//remove current company link
								var strOrg = strOrigCompany;
								recOrg = false;
								if(_swdoc.arrOrgs[strOrg]!=undefined)
								{
									var recOrg = _swdoc.arrOrgs[strOrg];//app.g.get_record("CONFIG_TYPEI", );
								}
								else
								{
									app.global.LogInfo("Process Import", "Get Organisation", strOrg, 1);
									var recOrg = app.g.get_record("company", strOrg);
									_swdoc.arrOrgs[strOrg] = recOrg;
								}

								if(recOrg)
								{
									app.cmdb.delete_meconfigrelation(recOrg.fk_cmdb_id,newCMDBID);  
								}

								if(strNewCompany!="")
								{
									var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");

									var strOrg = strNewCompany;
									recOrg = false;
									if(_swdoc.arrOrgs[strOrg]!=undefined)
									{
										var recOrg = _swdoc.arrOrgs[strOrg];//app.g.get_record("CONFIG_TYPEI", );
									}
									else
									{
										app.global.LogInfo("Process Import", "Get Organisation", strOrg, 1);
										var recOrg = app.g.get_record("company", strOrg);
										_swdoc.arrOrgs[strOrg] = recOrg;
									}
									if(recOrg!=false)
										app.cmdb.generate_meconfigrelation(recOrg.fk_cmdb_id,newCMDBID,strRel);  
								}
							}
							else if(boolSite)
							{
								//site has been set blank or unable to create site rel, create org rel with unchanged org
								if(strOrigCompany!="")
								{
									var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");

									var strOrg = strOrigCompany;
									recOrg = false;
									if(_swdoc.arrOrgs[strOrg]!=undefined)
									{
										var recOrg = _swdoc.arrOrgs[strOrg];//app.g.get_record("CONFIG_TYPEI", );
									}
									else
									{
										app.global.LogInfo("Process Import", "Get Organisation", strOrg, 1);
										var recOrg = app.g.get_record("company", strOrg);
										_swdoc.arrOrgs[strOrg] = recOrg;
									}
									if(recOrg!=false)
										app.cmdb.generate_meconfigrelation(recOrg.fk_cmdb_id,newCMDBID,strRel);  
								}
							}
						}

						//sla changed
						if(boolSLA)
						{
							//remove current sla link
							var strSLA = intOrigSLD;
							recSLA = false;
							if(_swdoc.arrSLAs[strSLA]!=undefined)
							{
								var recSLA = _swdoc.arrSLAs[strSLA];
							}
							else
							{
								app.global.LogInfo("Process Import", "Get SLA", strSLA, 1);
								var recSLA = app.g.get_record("itsmsp_slad", strSLA);
								_swdoc.arrSLAs[strSLA] = recSLA;
							}

							if(recSLA)
							{
								app.cmdb.delete_meconfigrelation(recSLA.fk_cmdb_id,newCMDBID);  
							}

							//if SLA has been set - not cleared, create new sla
							if(intNewSLD>0)
							{
								var strRel = "Uses";
								var strSLA = intNewSLD;
								recSLA = false;
								if(_swdoc.arrSLAs[strSLA]!=undefined)
								{
									var recSLA = _swdoc.arrSLAs[strSLA];
								}
								else
								{
									app.global.LogInfo("Process Import", "Get SLA", strSLA, 1);
									var recSLA = app.g.get_record("itsmsp_slad", strSLA);
									_swdoc.arrSLAs[strSLA] = recSLA;
								}
								if(recSLA!=false)
									app.cmdb.generate_meconfigrelation(recSLA.fk_cmdb_id,newCIid,strRel); 
							}
						}

					}
					//-- successfully created - so mark as processed
					//MessageBox(boolRes)
					_swdoc.mark_record_processed(updItem,!boolRes,strStageExtTable);	
				}	
				else
				{
					MessageBox("Failed to update staging record (" + updItem + "). Please contact your Supportworks Administrator.");
					_swdoc.mark_record_processed(updItem,true,"");	
					return false;
				}

			}
		}
	}
	return true;	
}

//--
//-- create new cmdb records
function process_create_records(strCreateItemIDs)
{
	var arrCITypes = new Array();
	var array_default_values = new Array();
	var strMissingType = "";
	var intCountMissingTypes=0;

	var arrDefaultCIValues = new Array();
	arrDefaultCIValues['isactivebaseline'] = 'Yes';
	arrDefaultCIValues['isunavailable'] = 'Yes';
	arrDefaultCIValues['isactive'] = 'Yes';
	arrDefaultCIValues['isauthorised'] = 'Yes';

	var arrCreateItems = strCreateItemIDs.split("©");
	var strItems = "";
	for(var x=0; x < arrCreateItems.length;x++)
	{
		if(strItems!="")strItems+=",";
		strItems += arrCreateItems[x];
	}	

	//-- get staged items
	var p={};
	p.stageditems = strItems;
	var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_stageditems",p);	
	while(oRS.Fetch())
	{
		//-- Check if staus values are provided
		var boolCmdbStatusSet = false;
		var boolFkStatusLevelSet = false;

		var strCmdbStatus = oRS.GetValueAsString("cmdb_status");
		if (strCmdbStatus!="")
		{
			boolCmdbStatusSet = true;
		}

		var strFkStatusLevel = oRS.GetValueAsString("fk_status_level");
		if (strFkStatusLevel!="")
		{
			boolFkStatusLevelSet = true;
		}

		//-- item id to create
		var createItem = oRS.GetValueAsString("ck_config_item");

		//-- get config_itemi_stage record
		var oCopyRec = _swdoc.convertRStoRec(oRS);

		//-- If type does not exist then skip record
		if(oCopyRec.import_newtype=="Yes")
		{
			if(strMissingType!="") strMissingType += "\r\n";
			strMissingType += oCopyRec.ck_config_type;
			intCountMissingTypes++;
			continue;
		}

		//-- perform insert into config_itemi table (ck_config_item, ck_config_type
		var strSkipFields = "import_analystid|import_datex|import_newtype|import_status|import_action";
		//MessageBox(11)
		var newCIid = app.g.copy_sw_record("config_itemi",oCopyRec,strSkipFields,new Array());
		//MessageBox(22)
		if(newCIid>0)
		{
			//-- now create CMDB me relationships for site, company and SLA if set
			//-- if customer has a site then relate it as a ci (so can see in vcm company -> site -> customer)
			var boolCheckOrg = true;

			if(oCopyRec['fk_site'] != "")    		
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCIRelationship");

				//if site is not already loaded, load site
				var strSite = oCopyRec['fk_site'];
				recSite = false;
				if(_swdoc.arrSites[strSite]!=undefined)
				{
					var recSite = _swdoc.arrSites[strSite];
				}
				else
				{
					app.global.LogInfo("Process Import", "Get Site", strSite, 1);
					var recSite = app.g.get_record("site", strSite);
					_swdoc.arrSites[strSite] = recSite;
				}

				if(recSite!=false)
				{
					app.cmdb.generate_meconfigrelation(recSite.fk_cmdb_id,newCIid, strRel);
					// PM00131778 - Remove code that prevents the creation of Org relationship
					//boolCheckOrg = false;
				}
			}
			if(boolCheckOrg)
			{
				if(oCopyRec['fk_company_id']!="")
				{
					var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgCIRelationship");

					var strOrg = oCopyRec['fk_company_id'];
					recOrg = false;
					if(_swdoc.arrOrgs[strOrg]!=undefined)
					{
						var recOrg = _swdoc.arrOrgs[strOrg];//app.g.get_record("CONFIG_TYPEI", );
					}
					else
					{
							app.global.LogInfo("Process Import", "Get Organisation", strOrg, 1);
						var recOrg = app.g.get_record("company", strOrg);
						_swdoc.arrOrgs[strOrg] = recOrg;
					}

					if(recOrg!=false)
						app.cmdb.generate_meconfigrelation(recOrg.fk_cmdb_id,newCIid,strRel);  
   				}
   			}

			if(oCopyRec['fk_sld']>0)
			{
				var strRel = "Uses";

				var strSLA = oCopyRec['fk_sld'];
				recSLA = false;
				if(_swdoc.arrSLAs[strSLA]!=undefined)
				{
					var recSLA = _swdoc.arrSLAs[strSLA];
				}
				else
				{					
					app.global.LogInfo("Process Import", "Get SLA", strSLA, 1);
					var recSLA = app.g.get_record("itsmsp_slad", strSLA);
					_swdoc.arrSLAs[strSLA] = recSLA;
				}

				if(recSLA!=false)
					app.cmdb.generate_meconfigrelation(recSLA.fk_cmdb_id,newCIid,strRel); 
			}


			//-- now update new CI with values from staging table
			//-- select fields from cmdb_stagefields
			var strUpdateCI = "";
			var strUpdateExt = "";
			var strExtTable = "";
			var strStageExtTable = "";
			var arrCols = new Array();

			var p={};
			p.stageditem = createItem;
			var oRSFields = app.g.get_sqrecordset("cmdb/process/stagedimport/get_item_stagefields",p);			
			while(oRSFields.Fetch())
			{
				//--
				var strNewValue = oRSFields.GetValueAsString("newvalue");
				var strColName  = oRSFields.GetValueAsString("targetcol");
				var strTableName  = oRSFields.GetValueAsString("targettable");
				if(app.g.dd_isnumeric(strTableName, strColName))
				{
					if(strNewValue<0)
						strNewValue = app.g.fix_epoch(strNewValue);
				}
				if(strTableName=="config_itemi")
				{
					if(strUpdateCI!="")strUpdateCI+="&";
					strUpdateCI += "_swc_" + strColName + "=" + pfu(strNewValue);
					arrCols.push(strColName);
				}
				else
				{
					strExtTable = strTableName;
					strStageExtTable = strExtTable+"_stage";
					if(strUpdateExt!="")strUpdateExt+="&";
					strUpdateExt += "_swc_" + strColName + "=" + pfu(strNewValue);	
				}
			}

			//-- ensure certain system defaults are set
			if(strUpdateCI!="")strUpdateCI+="&";
			strUpdateCI += "_swc_baselineid=" + newCIid;

			//-- If cmdb_status value is not provided set to 'Active'
			if (!boolCmdbStatusSet)
			{
				if(strUpdateCI!="") strUpdateCI+="&";
				strUpdateCI += "_swc_cmdb_status=Active";
			}
			//-- If fk_status_level value is not provided set to 'Operational'
			if (!boolFkStatusLevelSet)
			{
				if(strUpdateCI!="")strUpdateCI+="&";
				strUpdateCI += "_swc_fk_status_level=Operational";
			}		

			for(var strKey in arrDefaultCIValues)
			{
				if(!in_array(arrCols,strKey))
				{				
					if(strUpdateCI!="")strUpdateCI+="&";
					strUpdateCI += "_swc_" + strKey + "=" + arrDefaultCIValues[strKey];
				}	
			}

			//-- config_itemi
			if(strUpdateCI!="")
			{
				var boolRes = app.g.submittableupdate("config_itemi",newCIid,strUpdateCI)		
			}

			//create the staging record
			//-- check if has an extended table - if so check ext stage record values against main extended
			if (strExtTable!="")
			{
				if (boolRes)
				{
					//Add the values from the stagefields table
					if(strUpdateExt!="")
					{
						//-- extended table record is created in OnRecordAdded VPME for config_itemi so just need to  
						//-- Add the values from the stagefields table 
						var boolRes = app.g.submittableupdate(strExtTable,newCIid,strUpdateExt)	
					}
				}
				else
				{
					MessageBox("Failed to complete creating staging record (" + createItem + "). Please contact your Supportworks Administrator.");
				}
			}
			//-- successfully created - so mark as processed
			_swdoc.mark_record_processed(createItem,false,strStageExtTable);
		}
		else
		{
			MessageBox("Failed to create staging record (" + createItem + "). Please contact your Supportworks Administrator.");
			_swdoc.mark_record_processed(createItem,true,"");	
			return false;
		}

	}

	if(intCountMissingTypes>0)
		MessageBox(intCountMissingTypes+" items have been skipped as their specified configuration type does not exist.\r\nThe following missing configuration types were identified:\r\n\r\n"+strMissingType+"\r\n\r\nPlease create any appropriate types and re-process");

	return true;
}


//-- marks a single staging record as processed
function mark_record_processed(strItemID,boolFailed,strStageExtTable)
{
	if((_swdoc.MF().flg_delete.text=="1")&&(!boolFailed))
	{
		//-- add keys to be deleted from cmdb_stage table
		if(_swdoc.arrDeleteRecords['cmdb_stage']!=undefined)
		{
			_swdoc.arrDeleteRecords['cmdb_stage'] = _swdoc.arrDeleteRecords['cmdb_stage']+"," + strItemID;
		}
		else
		{
			_swdoc.arrDeleteRecords['cmdb_stage'] = strItemID;
		}

		//-- add keys to be deleted from cmdb_stagefields table
		if(_swdoc.arrDeleteRecords['cmdb_stagefields']!=undefined)
		{
			_swdoc.arrDeleteRecords['cmdb_stagefields'] = _swdoc.arrDeleteRecords['cmdb_stagefields']+"," + strItemID;
		}
		else
		{
			_swdoc.arrDeleteRecords['cmdb_stagefields'] = strItemID;
		}

		//-- add keys to be deleted from extended table
		if(strStageExtTable!="")
		{
			if(_swdoc.arrDeleteRecords[strStageExtTable]!=undefined)
			{
				_swdoc.arrDeleteRecords[strStageExtTable] = _swdoc.arrDeleteRecords[strStageExtTable]+"," + strItemID;
			}
			else
			{
				_swdoc.arrDeleteRecords[strStageExtTable] = strItemID;
			}
		}
	}
	else
	{
		var strKey= (boolFailed)?"Failed":"Completed";
		if(_swdoc.arrUpdateProcess[strKey]!=undefined)
		{
			_swdoc.arrUpdateProcess[strKey] = _swdoc.arrUpdateProcess[strKey]+"," + strItemID;
		}
		else
		{
			_swdoc.arrUpdateProcess[strKey] = strItemID;
		}
	}
}


//--
//-- Decline records
function decline_records(boolDeclineAll)
{
	if(boolDeclineAll)
	{
		//-- declining all those in list
		var strTitleItemIDs = app.g.sqllist_get_valuesdel(_swdoc.currentList, "ck_config_item", ",", "");
		var strURL = "declineditems=" +  strTitleItemIDs;
		var strDeclineItemIDs = app.g.sqllist_get_valuesdel(_swdoc.currentList, "ck_config_item", "©", "");

		//-- popup decline form to get reject reason
		app.OpenFormForAdd("cmdb_decline", "", strURL, true, function(aForm){
			if((aForm)&&(aForm.document.strReason!="")&&(aForm.document.boolApplied)){
				//-- loop through each and prompt for reason
				var arrItems = strDeclineItemIDs.split("©");
				for (var x=0;x < arrItems.length;x++)
				{
					_swdoc.decline_item(arrItems[x],aForm.document.strReason);
				}
			}
			else{
				if(aForm.document.boolApplied)	
					MessageBox("The list items were not declined as you did not provide a reason for rejecting them");
			}
		});
	}
	else
	{
		//-- declining all those in list
		var strTitleItemIDs = app.g.sl_getselected_valuesdel(_swdoc.currentList,"ck_config_item",",","");		
		var strURL = "declineditems=" +  strTitleItemIDs;
		var strDeclineItemIDs = app.g.sl_getselected_valuesdel(_swdoc.currentList,"ck_config_item","©","");		

		//-- popup decline form to get reject reason
		app.OpenFormForAdd("cmdb_decline", "", strURL, true, function(aForm){
			if((aForm)&&(aForm.document.strReason!="")&&(aForm.document.boolApplied)){
				//-- loop through each and prompt for reason
				var arrItems = strDeclineItemIDs.split("©");
				for (var x=0;x < arrItems.length;x++)
				{
					_swdoc.decline_item(arrItems[x],aForm.document.strReason);
				}
			}
			else{
				if (aForm.document.boolApplied)	
				   MessageBox("The list items were not declined as you did not provide a reason for rejecting them");
			}
		});
	}
}

//--
//-- decline item - create record in ci_declined and removes it from the staging table
function decline_item(strItemID,strReason)
{
	//-- get row pos then get other value
	var intRow = app.g.sl_findrow_byvalue(_swdoc.currentList,strItemID,"ck_config_item");
	if(intRow>-1)
	{
		var strType = _swdoc.currentList.GetItemText(intRow, "ck_config_type");
		var strAction = _swdoc.currentList.GetItemText(intRow, "import_action");
		var intDate = app.global.GetCurrentEpocTime();

		var oType  = app.g.get_record("config_typei", strType);
		//-- create decline record
		var p = {};
		p.ck_config_item = strItemID;
		p.ck_config_type = strType;
		p.import_action = strAction;
		p.datetimex	= intDate;
		p.rejectdesc = strReason;		
		if(app.g.submittableinsert("ci_declined",p))	
		{
			var strExtTable = oType.extended_table;
			if(strExtTable)
			{
				//-- 87558
				app.g.delete_tablebycol(oType.extended_table+"_stage","ck_config_item",strItemID);
			}
			//--F0104885
			//-- delete staging record
			return app.g.sl_deldbrow(_swdoc.currentList,intRow,'ck_config_item',null,"")
		}
	}
	return false
}

function isSystemField(colName)
{
	switch(colName)
	{
		case "baselineid":
		case "cmdb_status":
		case "flg_inherit_reqtypes":
		case "import_action":
		case "import_analystid":
		case "import_datex":
		case "import_newtype":
		case "import_status":		
			return true;
			break;
	}
	return false;
}


function convertRStoRec(oRS)
{
	var intCols = oRS.GetColumnCount();

	oRec = new Object();
	for (x=0; x < intCols;x++)
	{
		var colName = oRS.GetColumnName(x);
		var colValue = app.g.get_field(oRS,x);
		oRec[LC(colName)] = colValue;
	}
	return oRec;
}

