							btn_delete_OnPressed()
{


	//-- NWJ : Delete records that have been marked as processed
	var arrParams = new Array();
	arrParams["import_status"]="Completed";
	var oRS = app.g.get_tablerecordset_bycol("cmdb_stage",arrParams,false, "ck_config_item");
	var strConfigItemIDs = ""
	while(oRS.Fetch())
	{
		if(strConfigItemIDs!="")strConfigItemIDs+=",";
		strConfigItemIDs += app.g.get_field(oRS,"ck_config_item");
	}

	if(strConfigItemIDs!="")
	{
		var p = {};
		p.cis = strConfigItemIDs;
		app.g.submitsimplesql("cmdb_stage.btn_delete.delete_stage", p);

		var p = {};
		p.cis = strConfigItemIDs;
		p.sf= "cmdb_stage.btn_delete.get_exttables";
		var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_exttables", p);
		var strID = "";
		while(oRS.Fetch())
		{

			var strTable = app.g.get_field(oRS, "strtable");

			if(strTable =="") var strTables = app.g.get_field(oRS, "strtable");
			if(strTable !="") strTables += ","+app.g.get_field(oRS, "strtable");

		}
		arrTables = strTables.split(',');
		for (var i=0;i<arrTables.length;i++)
		{
			if(arrTables[i] != "undefined" & arrTables[i] != "")
			{
				var strTableDel = arrTables[i];
				strTableDel = strTableDel+"_stage";
				var strPK = dd.tables[LC(strTableDel)].PrimaryKey;
				if(!strPK) strPK = "ck_config_item";
				//-- TK Delete feilds from Extendted Tables
				var arrParams2 = new Array();
				arrParams2[strPK]=strConfigItemIDs;

				var oRS = app.g.get_tablerecordset_bycol(strTableDel,arrParams2,false);
				while(oRS.Fetch())
				{
					var p = {};
					p.cis = strConfigItemIDs;
					p.tbl = app.g.get_field(oRS,"strtable");
					app.g.submitsimplesql("cmdb_stage.btn_delete.delete_extstage", p);
				}
			}
		}


		//-- delete processed fields
		var p = {};
		p.cis = strConfigItemIDs;
		app.g.submitsimplesql("cmdb_stage.btn_delete.delete_stagefields", p);

		//Refresh the search results
		mainform.btn_search_OnPressed();
	}
}

							btn_determine_OnPressed()
{
	//Storaging the id, endx, status of the las row of the table cmdb_stage
	var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/get_stage_process");
	var boolRow = false;
	while(oRS.Fetch())
	{
		boolRow = true;
		var strID = app.g.get_field(oRS, "pk_id");
		var strEnd = app.g.get_field(oRS, "exe_endx");
		var strStatus = app.g.get_field(oRS, "status");
	}

	//If the status of the last row is complete we can start a new process
	if (strStatus=="Complete" || !boolRow){	
		var params = {};
		//--Set Analayst ID
		params.analystid = app.session.analystId;
		//--Stored Query
		var oRS = app.g.get_sqrecordset("cmdb/process/stagedimport/create_stage_process", params);
		MessageBox("Please check in History tab to verify the process has finished.");
	}
	//Other case we should wait until finishing
	else
		{
		MessageBox("There is currently a process running.");
		return false;
		}

}

