						function MF()
{
	return mainform;
}

function oReview()
{
	return contract_review;
}

						OnValidate()
{
	// TODO: Add your event handler code here
	if(contract_review.flg_messenger==1)
	{
		if(contract_review.msg_subject=="")
		{
			MessageBox('A messenger subject is required, please complete this field before saving.');
			return VALIDATE_FAIL;
		}
	}

	if(contract_review.flg_email_bo==1 || contract_review.flg_email_ito==1)
	{
		if(contract_review.mailbox=="" || contract_review.email_template=="")
		{
			MessageBox('Mailbox and Email template are required, please complete before saving.');
			return VALIDATE_FAIL;
		}
	}

	return VALIDATE_DEFAULT;
}

							fld_type_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(strValue=="Bespoke Time")
	{
		var thisTab = mainform.tType.tab;
		_swdoc.MF().tType.EnableTabItem(0,true);
		_swdoc.MF().tType.EnableTabItem(thisTab,false);
	}
	else if(strValue=="Periodical Time")
	{
		var thisTab = mainform.tType.tab;
		_swdoc.MF().tType.EnableTabItem(1,true);
		_swdoc.MF().tType.EnableTabItem(thisTab,false);
	}
	else if(strValue=="Monthly")
	{
		var thisTab = mainform.tType.tab;
		_swdoc.MF().tType.EnableTabItem(2,true);
		_swdoc.MF().tType.EnableTabItem(thisTab,false);
	}
	else if(strValue=="Weekly")
	{
		var thisTab = mainform.tType.tab;
		_swdoc.MF().tType.EnableTabItem(3,true);
		_swdoc.MF().tType.EnableTabItem(thisTab,false);
	}
	else if(strValue=="Available Units Threshold Breach")
	{
		var thisTab = mainform.tType.tab;
		_swdoc.MF().tType.EnableTabItem(4,true);
		_swdoc.MF().tType.EnableTabItem(thisTab,false);
	}
}

							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here

	var boolUpdate = (_swdoc.oReview().pk_id!=0);
	if(boolUpdate)
	{
		//-- updating
		//TabCond.ShowTabItem(2,true);	
		if(_swdoc.oReview().type!="Bespoke Time")
		 fld_type_OnValueChanged(_swdoc.oReview().type);	
		if(_swdoc.oReview().flg_messenger==1)
		{
			_een(_swdoc.MF().fld_msg_subject1, true);
			_een(_swdoc.MF().Label9, true);
			_een(_swdoc.MF().Label10, true);
			_een(_swdoc.MF().Field2, true);
			_ema(_swdoc.MF().fld_msg_subject1, true);
		}
		if(_swdoc.oReview().flg_email_ito==1 || _swdoc.oReview().flg_email_bo==1)
		{
			_een(_swdoc.MF().Label4, true);
			_een(_swdoc.MF().lb_mailbox1, true);
			_een(_swdoc.MF().lb_template1, true);
			_een(_swdoc.MF().Label5, true);
			_ema(_swdoc.MF().lb_mailbox1, true);
			_ema(_swdoc.MF().lb_template1, true);
		}
	}
	else
	{

		_swdoc.oReview().fk_id = _swdoc.GetArg("fk_id");
		_swdoc.oReview().fk_contract = _swdoc.GetArg("fk_contract");
		_swdoc.oReview().flg_sla = _swdoc.GetArg("flg_sla");

		_swdoc.UpdateFormFromData();
	}

	if(_swdoc.oReview().flg_sla<1)
	{
		_ete(_swdoc.MF().Label7, "Email Group Manager");
		_ete(_swdoc.MF().Label8, "Email Service Manager");

	}

	if(_swdoc.oReview().flg_sla>1)
	{
		_ete(_swdoc.MF().Label7,"Email Service Desk");
		_ete(_swdoc.MF().Label8,"Email Owner");

	}
	_swdoc.ResetModiedFlag("contract_review");

}

							messenger1_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	_een(_swdoc.MF().fld_msg_subject1, strValue==1);
	_een(_swdoc.MF().Label9, strValue==1);
	_een(_swdoc.MF().Label10, strValue==1);
	_een(_swdoc.MF().Field2, strValue==1);
	_ema(_swdoc.MF().fld_msg_subject1, strValue==1);
	return true;
}

							lb_mailbox1_OnLeftClick()
{
	//-- get mailbox list

	if(lb_mailbox1.pickList=="")
	{
		//-- call php to get mailbox list
		var strURL = "php/itsm/get_mailboxlist.php"; 
		var strResult = app.g.submit_php(strURL,true);
		lb_mailbox1.pickList = strResult;
	}


	return true;
}

							lb_template1_OnLeftClick()
{
	//-- get template list

	if(lb_template1.pickList=="")
	{
		//-- call php to get mailbox list
		//MessageBox(lb_mailbox1.text)
		var strURL = "php/itsm/get_templatelist.php?mailboxname=" + lb_mailbox1.text; 
		var strResult = app.g.submit_php(strURL,true);
		lb_template1.pickList = strResult;
	}

	return true;
}

