///////////////////////////////////////////////////////////////////////////////////
// this is the UpdateCalls function that is called when a user clicks
// on either Update Call or Update and Assign.  It is being
// defined here so that we can use it for either.
// this function takes as an argument the analyst, if any, to whom you are assigning the call

function AcceptCalls(funcCallback)
{
	// Set the SLA text to null if the SLA field is not applicable (multiple calls, etc.)
	if (mainform.tb_sla.visible == false || _swdoc.arrCallRefs.length > 1)
	{
		strSLAtext = "^^^";
	} 
	else
	{
		strSLAtext = mainform.tb_sla.text;
	}

	//-- RJC - 14.04.2010 - We might want to update call values before updating i.e. for customisation
	//-- update opencall fields - we need to do this by looping through form elements	
	//-- problem is BeginCallValueUpdates doesnt accept multiple callrefs so have to do a call update per callref
	var arrUpdateCallValues = new Array();
	var boolCallUpdates = false;
	var strOpencall = "";
	var rec = _swdoc.GetRecord("opencall");			
	if(rec)
	{
		var nColCount = rec.GetCount();
		for(x=0; x<nColCount; x++)
		{
			boolSkip = false;
			var strColumn = rec.GetColumnName(x);
			if(strColumn=="itsm_sladef")
			{
				var strValue = rec.GetValue(x);
				if(strValue=="")
				{
					boolSkip = true;
				}
				if(strValue<1)
					boolSkip = true;
			}	

			if(!rec.IsModified(x) || boolSkip)
			{
				continue;
			}
			boolCallUpdates = true;
			if(strColumn=="itsm_sladef")
			{
				var strValue = rec.GetValue(x);
				if(strValue!=_swdoc.origSLA)
				{
					_swdoc.newSLA = strValue;
				}

			}
			arrUpdateCallValues[strColumn] = rec.GetValue(x);
		}

		//-- F0097408
		if(_swdoc.newSLA!=_swdoc.origSLA)
		{
			var oldSLA = app.g.get_record("itsmsp_slad", _swdoc.origSLA);
			var currSLA = app.g.get_record("itsmsp_slad", _swdoc.newSLA);
			var strText = "";
			if(!oldSLA)
			{
				strText = "\nThe SLA was changed from  to "+currSLA.slad_id+".";
			}else
			{
				strText = "\nThe SLA was changed from "+oldSLA.slad_id+" to "+currSLA.slad_id+".";
			}

			_swdoc.updatedb.updatetxt += strText;
			arrUpdateCallValues['itsm_slaname'] = currSLA.slad_id;
		}	

		//-- if we have opencal l values to update
		if(boolCallUpdates)
		{
			//-- for each opencall field that needs updating send
			for (fieldName in arrUpdateCallValues)
			{
				strOpencall += "<"+fieldName+">"+ app.global.PrepareForXML(arrUpdateCallValues[fieldName])+"</"+fieldName+">";
			}				
		}
	}

	if(strOpencall!="")
	{
		strOpencall = "<opencall>"+strOpencall+"</opencall>";	
		for(var refcount = 0; refcount < _swdoc.arrCallRefs.length;refcount++)
		{
 			var ocxmlmc = new XmlMethodCall;

			ocxmlmc.SetValue("callref", _swdoc.arrCallRefs[refcount]);
			ocxmlmc.SetParamAsComplexType("additionalCallValues", strOpencall);	
			if(ocxmlmc.Invoke("helpdesk", "updateCallValues"))
			{
	    		//-- get the result
				var strXML = (app.bWebClient)? ocxmlmc._lastresult:ocxmlmc.GetReturnXml();
				var objRes = XMCResult(strXML);
				if(!objRes.success)
				{
					MessageBox("Unable to send opencall values to the server.");
					funcCallback(false);
					return;
	    		}	
			}
			else
			{
				MessageBox("Unable to send opencall values to the server.");
				funcCallback(false);
				return;
			}
		}
	}

	var strAction = "updateAndAcceptCalls";
	var updateDBRec = _swdoc.GetRecord("updatedb");

	// -- Workout "timeOfAction" param
	var strBackDate = _swdoc.nBackdatePeriod;
	if(strBackDate==0 && _swdoc.boolSetToMaxBackdatePeriod)
	{
		strBackDate = app.global.GetCurrentEpocTime() - app.session.GetMaxBackdatePeriodAllowed();
	}

 	var xmlmc = new XmlMethodCall;

 	var arrCallref = _swdoc._form["callrefs"].split(",");
 	for(x in arrCallref)
		xmlmc.SetValue("callref", arrCallref[x]);		
	xmlmc.SetValue("timeSpent",  mainform.tm_timespent.value);		
	xmlmc.SetValue("description", updateDBRec.updatetxt);	
	if(mainform.flg_updatepublic.text=="1")
		xmlmc.SetValue("publicUpdate", true);
	if(strBackDate!=0) xmlmc.SetValue("timeOfAction", app.global.GetAsISO8601TimeString(strBackDate));
	if(updateDBRec.udsource!="")
		xmlmc.SetValue("updateSource", updateDBRec.udsource);		
	if(updateDBRec.udcode!="")
		xmlmc.SetValue("updateCode", updateDBRec.udcode);	
	//only set for update and update and assign to analyst
	if(mainform.flg_slaresponse.text=="1")
	{
		xmlmc.SetValue("markAsSLAResponse", true);	
	}
	if(_swdoc.boolChangeSLA)
	{
		if(strSLAtext!="^^^")
			xmlmc.SetValue("priority", strSLAtext);	
	}	
	//don't send the target fix time unless changed
	if (_swdoc.bTargetFixTimeChanged)
		xmlmc.SetValue("targetFixTime", mainform.tb_target.value);
	for(i=0; i<mainform.fl_attachslist.rowCount();i++)
	{
 		var strFileName = mainform.fl_attachslist.GetItemText(i,0);
		var strData = app.global.LoadFileInBase64(mainform.fl_attachslist.GetItemText(i,3));
		var strXML = "<fileName>"+strFileName+ "</fileName>";
		if(!app.bWebClient)
			strXML += "<fileData>"+strData+"</fileData>";
		xmlmc.SetParamAsComplexType("fileAttachment",strXML);	
    }

	var strUpdatedb = "";
	// Send each bound value in the updatedb table that has been modified
	if(rec = _swdoc.GetRecord("updatedb"))
	{
		var nColCount = rec.GetCount();
		for(x = 0; x < nColCount; x++)
		{
			if(!rec.IsModified(x))
				continue;

			var fieldName = rec.GetColumnName(x);
			if(fieldName=="udsource" || fieldName =="udcode" || fieldName =="updatetxt")
				continue;
			strUpdatedb += "<"+fieldName+">"+app.global.PrepareForXML(rec.GetValue(x))+"</"+fieldName+">";
		}
	}
	if(strUpdatedb!="")
	{
		xmlmc.SetParamAsComplexType("extraUpdateDbValues", strUpdatedb);	
	}

	if(xmlmc.Invoke("helpdesk", strAction))
	{
	    //-- get the result
		var strXML = (app.bWebClient)? xmlmc._lastresult:xmlmc.GetReturnXml();
		var objRes = XMCResult(strXML);
		if(!objRes.success)
		{
			MessageBox("The update transaction was rejected by the server.");
			funcCallback(false);
			return;
	    }	
	    else
	    {
			var myXmlFile = new XmlFile(); 
			bRet = myXmlFile.loadFromString(strXML); 
			var arrChildCallInfo = null;
			var arrCallInfo = new Array();
			var strDocs = "";
			for (count = 0; count < myXmlFile.methodCallResult.params.length; count ++) 
			{
				if(myXmlFile.methodCallResult.params[count].nodeName=="callActionStatus")
				{  
					var boolSuccess = myXmlFile.methodCallResult.params[count]['success'].nodeValue;
					if(boolSuccess=="false")
					{
						var strMessage =  myXmlFile.methodCallResult.params[count]['message'].nodeValue;
						MessageBox(strMessage);
						funcCallback(false);
						return;
					}
					var strCallref = myXmlFile.methodCallResult.params[count]['callref'].nodeValue;
					var strIndex = myXmlFile.methodCallResult.params[count]['udIndex'].nodeValue;
					arrCallInfo[strCallref] = strIndex;
				}
				else if(myXmlFile.methodCallResult.params[count].nodeName=="callFileAttachment")
				{
					var intCount = arrCallInfo.length;
					if(intCount==1)
					{
						try
						{
							var updateId = myXmlFile.methodCallResult.params[count]['updateId'].nodeValue;
							if(updateId==arrCallInfo[strCallref])
							{
								var pathName = myXmlFile.methodCallResult.params[count]['pathName'].nodeValue;
								var fileName = myXmlFile.methodCallResult.params[count]['fileName'].nodeValue;
								var fileSize = myXmlFile.methodCallResult.params[count]['fileSize'].nodeValue;
								if(strDocs!="") strDocs +="\r\n";
								strDocs +='displayname="'+fileName+'"; path="'+pathName+'"; size="'+fileSize+'";';
							}
						}
						catch(e)
						{

						}
					}
				}
			}

			// If we have been invoked to update a call from an e-mail message, this update will 
			// only relate to a single call. We can only handle e-mail audit trail if we are working
			// on a single-call update. 
			if(_swdoc.arrCallRefs.length == 1 && 			// Are we only dealing with a single call refernce?
				_swdoc._form["messagesource"] && 	// Are we woring from an e-mail message?
				dd.GetGlobalParamAsNumber("Email Audit Trail/StoreEmailAuditTrail")) // Are we configured?
			{
				app.g.attach_message_to_call(_swdoc._form,_swdoc.arrCallRefs[0],arrCallInfo[_swdoc.arrCallRefs[0]]);
			}

    		// if we want to send an email to the customer AND there are file attachments, obtain the 
    		// attachments to attach to the email as well
    		var flSessionFilesInfo = "";
    		if(mainform.flg_emailcustomer.value && app.global.IsConnectedToMailServer())
    		{
				//-- webclient requires callref.udindex in order to retrieve fileinfo that it can use
				if(app.bWebClient) strDocs = _swdoc.arrCallRefs[0] + "." + arrCallInfo[_swdoc.arrCallRefs[0]];

				// Now, compose an email to the customer				
				app.global.ComposeCallUpdateEmail(arrCallInfo, 					// Array: Call reference-UdIndex
										_swdoc.updatedb.updatetxt, 	// Message text
										strDocs, 		// File attachments
										_swdoc.updatedb.timespent,
										"UpdateCallMailTemplate", 		// Template name
										TEMPLATE_UPDATECALL,			// Template type
										function()
										{
											funcCallback(true);
											return;
										});
			}
			else
			{
				funcCallback(true);
				return;
			}
		}	
	}
	else
	{
		funcCallback(true);
		return;
	}
}

// Validate the information entered into the form by the user. Return 'true' if
// the data is OK, of 'false' otherwise.  MessageBox's may be shown to indicate
// validation failure
function ValidateForm()
{
	//validate timespent field
	if(mainform.tm_timespent.text == 0) 
	{
		MessageBox("You must enter the amount of time spent", MB_OK);
		return false;	// Make sure that the time spent is entered
	}

    //validate sla field
	if(mainform.tb_sla.mandatory == true && mainform.tb_sla.text == "")
	{
		if(!app.global.ValidateSLAName(mainform.tb_sla.text))
		{
			MessageBox("You must enter a valid priority", MB_OK);
			return false;	// Make sure that the priority is set
		}
	}

	//validate description field
	if(mainform.tb_description.text == "")
	{
		MessageBox("You must enter an update description", MB_OK);
		return false;	// Make sure we have some update text
	}

	//validate target field
	if(mainform.tb_target.mandatory == true && mainform.tb_target.text == "")
	{
		var Message = "The Target Fix Time must be completed before you can apply this action";
		MessageBox(Message);
		return false;	
	}

	//validate actionsource field
	if(mainform.tb_actionsource.mandatory == true  && mainform.tb_actionsource.text.length == 0)
	{
		var Message = "The field '" + mainform.lbl_actionsource.text + "' must be completed before you can apply this action";
		MessageBox(Message);
		return false;
	}

	//validate general update field
	if(mainform.tb_generalupdate.mandatory == true && mainform.tb_generalupdate.text.length == 0)
	{
		var Message = "The field '" + mainform.lbl_actiontype.text + "' must be completed before you can apply this action";
		MessageBox(Message);
		return false;
	}

	return true;
}	//end _swdoc.ValidateForm

OnFormLoaded()
{
	// Set form with defaults set:
	_een(flg_emailcustomer,true);
 	_ero(flg_emailcustomer,false);

	// set update date to current time
	_eva(dt_updatedate,(_swdoc.dtCurrentTime.getTime()/1000));

	// if analyst has permission to backdate calls, make update time editable
	if(app.session.CanBackdateCallActions())
	{
		_ero(dt_updatedate,false);
		_een(lbl_datetime,true);
	}


	// If the analyst's default is to send an email on update, check the email box
	// else we uncheck the box
	if(app.session.IsDefaultOption(ANALYST_DEFAULT_UPDATESENDEMAIL))
	{
		_eva(flg_emailcustomer,"1");
	}
	else
	{
		_eva(flg_emailcustomer,"0");
	}

	//If we are not connected to a mail server then reset and disable the email flag
	if(app.global.IsConnectedToMailServer() == false)
	{
		_eva(flg_emailcustomer,"0");
		_een(flg_emailcustomer,false);
	}

	// if the analyst's default is to make the update private, then check the Make Private box.
	_eva(flg_updatepublic,app.session.IsDefaultOption(ANALYST_DEFAULT_UPDATEPRIVATE));

	var db = new SqlQuery;



	//-- NWJ - 14.02.2006 - run check to see if we can mark update as SLA response
	app.g.check_sla_response_flag(_swdoc._form["callrefs"],_swdoc.MF().flg_slaresponse);

	var db = new SqlQuery;	

	//if this form has been called with attachments, add them to the attachments list
	var index = 0;
	var bAreAttachments = false;
 	while(_swdoc._form["attach"+index]) 
 	{
 		bAreAttachments = true;
    	fl_attachslist.AddAttachment(_swdoc._form["attach" + index]);
		index++;
 	}

 	//if the analyst does not have permissions to add files to calls, make the file attachments box invisible
 	//UNLESS there are file attachments being included from the email which called this form, in which case
 	//show the file attachments, but make the Add and Remove buttons invisible

 	if(!app.session.HaveRight(ANALYST_RIGHT_A_GROUP,ANALYST_RIGHT_A_CANATTACHFILESTOCALLS))
 	{
 		if(!bAreAttachments)
 		{
 			Tab1.ShowTabItem(1,false);
 		}
 		else
 		{
 			_evi(btn_addfile,false);
 			_evi(btn_removefile,false);
 		}
 	}


	/////////////////////////////////  SLAs ////////////////////////////////////////////
	// display the SLA and fix time for the first call ref.  
	if (_swdoc.arrCallRefs.length > 1)
	{
		_evi(tb_sla,false);
		_evi(tb_slamultiple,true);
		_een(lbl_sla,false);

		_evi(tb_sladef,false);
		_evi(tb_sladefmultiple,true);
		_een(lbl_sladef,false);
	}
	else
	{
		var strParams = "crf="+pfu(_swdoc.arrCallRefs[0]);
		db.InvokeStoredQuery("form/acceptcall/get_sla_details", strParams);
		if(db.Fetch())
		{
			if(db.GetValueAsString(0))
			{
				_eva(tb_sla,db.GetValueAsString(0));
				_ete(tb_sla,db.GetValueAsString(0));
			}

			if(db.GetValueAsString(1) == "_THIRDPARTY")
			{
				_ete(flg_slaresponse,"Mark as Third Party SLA Response");
			}

			if(db.GetValueAsString(2))
			{
				_slf(tb_sla , "_swc_flg_sla=1&_swc_fk_slad="+ db.GetValueAsNumber(2));
				opencall.itsm_sladef = db.GetValueAsNumber(2);
				_swdoc.origSLA = db.GetValueAsNumber(2);
				_swdoc.newSLA = _swdoc.origSLA;
				_swdoc.UpdateFormFromData();
			}
		}
	}

	db.Reset();

	// if the analyst does not have permissions to change SLAs or call statuses, or if there is more than one call passed, disable the SLA box & text.
	// note:  because Javascript will not evaluate right-hand expressions unless all prior expressions return false, the GetCallStatus
	// at the end of this expression will not be called unless arr.CallRefs.length is <= 1.
	if(!app.session.HaveRight(ANALYST_RIGHT_A_GROUP,ANALYST_RIGHT_A_CANCHANGEPRIORITY) || !app.session.HaveRight(ANALYST_RIGHT_A_GROUP,ANALYST_RIGHT_A_CANMODIFYCALLS) || _swdoc.arrCallRefs.length > 1 || 
		_swdoc.bOnHold || _swdoc.bNotOpen)
	{
		_een(lbl_sla,false);
		_een(tb_sla,false);

		_een(lbl_sladef,false);
		_een(tb_sladef,false);
		_swdoc.boolChangeSLA = false;
	}else
	{
		_swdoc.boolChangeSLA = true;
	}



   	// if the data dictionary calls the priority something other than "SLA", change the label
   	if(dd.tables["updatedb"].IsColumnDefined("priority"))
   	{
   		_ete(lbl_sla,dd.tables["updatedb"].columns["priority"].DisplayName);
	}

	//////////////////////// FIX BY /////////////////////
	// set the Fix By time to the current date, unless there is another one set already
	if (_swdoc.arrCallRefs.length >1)
	{
		_evi(tb_target,false);
		_evi(tb_targetmultiple,true);
	}
	else
	{
		var strParams = "crf="+pfu(_swdoc.arrCallRefs[0]);
  		db.InvokeStoredQuery("form/acceptcall/get_fix_details", strParams);
   		if(db.Fetch())
		{
			_eva(tb_target,db.GetValueAsString(0));
			// if fix_ctr > 0, then make this editable
			if (db.GetValueAsNumber(1))
			{
				_ero(tb_target,false);
				_een(lbl_target,true);
			}		
		}
		else
		{
			_eva(tb_target,app.global.GetCurrentEpocTime());
		}

		db.Reset();
	}	

	//ES - F0111089 - Display label within Accept Call Form if Organisation is out of Support
	if (_swdoc.arrCallRefs.length >1)
	{
		//multiple calls
		var intExpiryEpoch = new Array();
		var orgName = new Array();
		var supportStatus = new Array();
		var warningMsg = "Warning: The organisation for one or more of the selected calls is no longer supported.";

		for(var i=0; i<_swdoc.arrCallRefs.length; i++)
		{
			db.InvokeStoredQuery("company/get_call_organisation","in_callref="+_swdoc.arrCallRefs[i]);
			if (db.Fetch())
			{
