mbtn_company_contract_actions1_OnMenuItem(strName, nItemID)
{
    var iAddContract = 1;
	var iDeleteContract = 2;

	//-- Add Contract
	if(nItemID == iAddContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- create a new contract for company
		var strURL = "fk_company_id=" + pfu(_swdoc.oSupplier().company_id)+"&type=supplier";
  		app.g.sl_openformforadd(sl_contracts1,"Tf3PartyContract", strURL, 1,null, function()
  		{
  			_swdoc.load_contracts();
  		});  		
	}
	else if(nItemID == iDeleteContract)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract

		var strContractID = app.g.sl_getrawcolvalue(sl_contracts1,-1,"pk_contract_id");
		if(strContractID=="") return;

		var strParams = "cid="+pfu(strContractID);
		var nCount = app.g.sqs_rowcount("count/supplier_calls", strParams);
		if(nCount>0)
		{
			MessageBox("There are currently "+nCount+" call(s) assigned to this contract.\n\n Please re-assign them before you continue.",16);
			return false;
		}

		var strSLAID = app.g.sl_getrawcolvalue(sl_contracts1,-1,"fk_priority");
		if (strContractID!="")
		{
			if (!confirm("Are you sure you want to delete the selected contract ?")) return;

			//-- delete the actual contract
			app.g.sqlexecute_delete("contract",strContractID);
			app.global.DeleteSla(strSLAID);
			app.global.RefreshHelpdeskAnalystsTree();
			_swdoc.MF().sl_contracts1.Refresh()
			_swdoc.MF().sl_contracts1.SetRowSelected(0);

		}
	}
}

