mbtn_service_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignService = 1;
	var iRemoveService = 2;	

	if(nItemID == iAssignService)
	{
		var strURL = "";
		app.cmdb.search_for_service(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			var strParams = "id="+pfu(supplier.company_id)+"&keys="+ sResults.selectedkeys;
			app.g.submitsqp("form/supplier/mark_as_supplier",strParams);
			sl_results2.Refresh();
			return;
		});
    }
	else if(nItemID == iRemoveService)
	{
		var strCIids = app.g.sl_selectedkeys(sl_results2,'pk_auto_id',true,",");
		if(strCIids=="")  return;

		if(!confirm("Are you sure you want to dis-associate the selected Service(s) from this Supplier?"))return;

		var strParams = "keys="+ strCIids;
		app.g.submitsqp("form/supplier/clear_supplier",strParams);
		sl_results2.Refresh();
	}





}

mbtn_view_ci1_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;

   	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_results1,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_results1,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}


}

mbtn_ci_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	


	if(nItemID == iAssignCI)
	{
		var strURL = "fk_company_id=";// + oCust().fk_company_id;
		app.cmdb.search_ci(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
			var strParams = "id="+pfu(supplier.company_id)+"&keys="+ sResults.selectedkeys;
			app.g.submitsqp("form/supplier/mark_as_supplier",strParams);
			sl_results1.Refresh();
			return;
		});

    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove customer and ci relation
		var strCIids = app.g.sl_selectedkeys(sl_results1,'pk_auto_id',true,",");
		if(strCIids=="")  return;

		//select supplier contracts
		var strContractIDs = "";
		var strParams = "id="+pfu(supplier.company_id)+"&keys="+pfu(strCIids);
		var oRS = app.g.get_sqrecordset("form/supplier/get_contracts",strParams);
		if(oRS.Fetch())
		{
			MessageBox("Configuration Item(s) selected to dis-associate are assigned to a Support Contract.\nDis-associate from Support Contract before removing from supplier.");
			return false;
		}

		if(!confirm("Are you sure you want to dis-associate the selected Configuration Items(s) from this Supplier?"))return;

		var strParams = "keys="+ strCIids;
		app.g.submitsqp("form/supplier/clear_supplier",strParams);
		sl_results1.Refresh();

		//_swdoc.refresh_customer_cilist();
	}





}

