	var boolAllowed  =testregex(strValue,"^[a-zA-Z0-9]+$");
	if(!boolAllowed)
	{
		var strMessage = "Supportworks does not allow the use of spaces, periods or other non alpha numeric characters in the Company ID field.";
		strMessage += "The Company ID must be 16 characters or less in length. An underscore as the first character is also not allowed.\n\n";
		strMessage += "Use only A-Z, a-z and 0-9\n\nPlease re-enter the company ID.";
		MessageBox(strMessage,16)
		supplier.company_id = _swdoc.strCompanyId;
		_swdoc.UpdateFormFromData();
		return false;
	}else
	{
		_swdoc.strCompanyId = strValue;
	}

}

