function refresh_contract_cis()
{
	var strCMDBids = app.cfg.get_contract_cikeys(_swdoc.oContract().pk_contract_id,true);
	_slf(mainform.sl_results1 ,"pids="+strCMDBids+"&sf=cis_notlikemeandactive"); //-- with static filter
}

//-- check contract state
function check_contract_state()
{	
	//-- Check end date is after start date
	if((app.g.fix_epoch(_swdoc.oContract().end_datex)>0)&&(app.g.fix_epoch(_swdoc.oContract().start_datex)>0))
	{
		if(app.g.fix_epoch(_swdoc.oContract().end_datex) < app.g.fix_epoch(_swdoc.oContract().start_datex))
		{
			app.global.MessageBox("The 'Expires On' date must be after the 'Valid From' date.", MB_ICONEXCLAMATION | MB_OK)
			return false;
		}
	}

	var boolSave = false;
	if(_swdoc.boolInserting==undefined)boolSave = true;
	//-- if the contract end date is less than todays date then set flag to 0
	//-- or if the start date is greater than todays date (not started yet)
	if ( (app.g.fix_epoch(_swdoc.oContract().end_datex) < app.global.GetCurrentEpocTime()) || (app.g.fix_epoch(_swdoc.oContract().start_datex) > app.global.GetCurrentEpocTime())	)			
	{
		//-- if system has not flagged it yet set flag and save
		if (_swdoc.oContract().flg_valid=="1")
		{	
			_swdoc.oContract().flg_valid="0";
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
	else
	{
		if (_swdoc.oContract().flg_valid==0)
		{	
			_swdoc.oContract().flg_valid="1";
			_swdoc.oContract().flg_ewemail_sent="0";			
			_swdoc.oContract().flg_expemail_sent="0";						
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}

	return true;
}

//-- SG 24/07/12, F0103808
//-- Translates Decrement Method flag in to text
function get_dec_method(decMethodFlag)
{
	var decMethodText = "";
	switch(decMethodFlag)
	{
		case "0":
			break;
		case "1":
			decMethodText = "Use Default Support Unit Decrement Value";
			break;
		case "2":
			decMethodText = "Prompt Analyst on Ticket Resolution/Closure";
			break;
		case "3":
			decMethodText = "Take from Contract/Service Association";
			break;
		default:
			decMethodText = false;
			break;
	}
	return decMethodText;
}

function update_pricing()
{
	if(_swdoc.boolRefreshPricing)
	{
		if(_swdoc.boolChanges)
			if(confirm("The pricing details are about to be updated, would you like to save your changes?\n(All unsaved changes will be lost)"))
				_swdoc.Save(0);
		var arrSummaryData = app.g.update_contract_pricing_summary(_swdoc.oContract().pk_contract_id);
		_swdoc.LoadRecordData("swdata", "contract", _swdoc.oContract().pk_contract_id);
		_swdoc.UpdateFormFromData();
		_slf(_swdoc.MF().sl_pricing , "_swc_fk_contract_id="+_swdoc.oContract().pk_contract_id);
		_swdoc.boolRefreshPricing	= false;
		return;
	}
}

function setup_services()
{
	var boolOneSelected = app.g.sl_getselected_count(_swdoc.MF().sl_results2)==1;
	_een(_swdoc.MF().mbtn_view_service, boolOneSelected);

	if(boolOneSelected)
	{
		var intRelKey = app.g.sqllist_getselected_value(_swdoc.MF().sl_results2, 1);
		if(intRelKey=="")return false;

		var p = {};
		p.fk_parent_id = contract.fk_cmdb_id;
		p.fk_child_id = intRelKey;
		p.fk_parent_type = 'ME->CONTRACT';
		p.fk_child_type = 'ME->Service';	
		var oRec = app.g.get_tablerecord_bycol("config_reli",p);	
		if(oRec)
		{
	    	_ete(_swdoc.MF().fld_support_units, oRec.support_units);
	    	_ecol(_swdoc.MF().fld_support_units, "black");
    	}
    }
	else if(app.g.sl_getselected_count(_swdoc.MF().sl_results2)==0)
	{
		_ete(_swdoc.MF().fld_support_units, "No Service Selected");
		_ecol(_swdoc.MF().fld_support_units, "black");
	}
		else{
		_ete(_swdoc.MF().fld_support_units, app.g.sl_getselected_count(_swdoc.MF().sl_results2) + " associations selected");
		_ecol(_swdoc.MF().fld_support_units, "red");
	}
}

						OnDataSaved()
{
	_swdoc.boolChanges = false;
	if(!_swdoc.boolInserting)
	{
		var strText = "";
		for(var intNumberOfInsert=0;intNumberOfInsert<_swdoc.arrDiary.length;intNumberOfInsert++)
		{
			arrDetails = _swdoc.arrDiary[intNumberOfInsert]
			if(strText!="")strText += "\n"
			if(arrDetails[0] == "Support Unit Decrement Method")
			{
				strText += arrDetails[0]+" has been modified from '"+_swdoc.get_dec_method(arrDetails[1])+"' to '"+_swdoc.get_dec_method(arrDetails[2])+"'";
			}
			else{
				strText += arrDetails[0]+" has been modified from '"+arrDetails[1]+"' to '"+arrDetails[2]+"'";
			}
		}
		if(strText!="")
		{
			var xI = new oXmlmcInsert("contract_diary");
			xI.setfield("fk_contract_id",contract.pk_contract_id);
			xI.setfield("updatetxt",strText);
			xI.setfield("udcode",'Detail Update');
			xI.setfield("analystid",app.session.analystId);
			xI.setfield("updatedonx",app.g.todaysdate_epoch());			
			xI.setfield("comments",_swdoc.MF().fld_comments.value);			
			xI.setfield("units_remain",_swdoc.MF().tb_units_avail.value);															
			xI.execute(true);
		}

	}
	_swdoc.boolInserting=false;
	_swdoc.MF().Tab1.EnableTabItem(1,!_swdoc.boolInserting);
	_swdoc.MF().Tab1.EnableTabItem(2,!_swdoc.boolInserting);
	_swdoc.MF().Tab1.EnableTabItem(3,!_swdoc.boolInserting);


}

						function setup_history()
{
	//RJC 05.01.09
	//ITSM 2.4 FS 13.7
	//Create array of insert details
	var arrInserts = new Array();

	//-- get matching contract record
	var p = {};
	p.pk_contract_id = contract.pk_contract_id;
	var oLiveContractRec = app.g.get_tablerecord_bycol("contract",p);
	if(!oLiveContractRec)return;

	var liveVal="";
	//-- for each field check if diff from contract
	for (x=0; x < dd.tables['contract'].columns.length;x++)
	{

		var colName = dd.tables['contract'].columns[x].Name;
		if(colName=="contract_notes")continue;

		//make sure the field exists
		if(oLiveContractRec[colName]!=undefined)
		{
			//-- if values differ and value is not empty put into a field table
			//-- if numeric value and = 0 then ignore
			var FORM_VALUE = contract[colName];
			if((app.g.dd_isnumeric('contract',colName))&&(FORM_VALUE==0))continue;

			var	dispFormVal = FORM_VALUE;
			var liveVal = oLiveContractRec[colName];
			var dispLiveVal = liveVal;
			if(app.g.dd_isnumeric('contract',colName))
			{
				if(FORM_VALUE<0)
				{
					FORM_VALUE= app.g.fix_epoch(FORM_VALUE);
					dispFormVal = app.g.convert_epochddmmyyyy(FORM_VALUE);
					dispLiveVal = app.g.convert_epochddmmyyyy(liveVal);
				}
			}

			if((liveVal!=FORM_VALUE))
			{
				arrInserts.push(new Array( app.g.dd_fieldlabel('contract',colName),app.g.pfs(dispLiveVal) , app.g.pfs(dispFormVal)))
			}
		}	
	}

	_swdoc.arrDiary = arrInserts;
}

							OnFormLoaded(bDataReloaded)
{
	//-- insert / update actions
	_swdoc.boolInserting = (_swdoc.oContract().pk_contract_id=="");

	if(_swdoc.boolInserting)
	{
		//-- set fields from args, update doc and reste modified flag
		_swdoc.temp_key = app.session.analystId + "_" + app.global.GetCurrentEpocTime();
		_swdoc.oContract().fk_company_id= unescape(_swdoc.GetArg("fk_company_id"));
		_swdoc.oContract().type = unescape(_swdoc.GetArg("type"));
		_swdoc.oContract().flg_support_unlimited=1;
		_swdoc.ResetModiedFlag('contract');
	}
	else
	{
		_swdoc.temp_key = _swdoc.oContract().pk_contract_id;
		//-- Update Pricing Summary
		app.g.update_contract_pricing_summary(_swdoc.oContract().pk_contract_id);
		_swdoc.UpdateFormFromData();

		if(_swdoc.oContract().flg_support_unlimited==1)
		{
			_een(_swdoc.MF().tb_units_avail,false);
		}
		else
		{
			_een(_swdoc.MF().tb_units_avail,true);
		}

	}


	Tab1.EnableTabItem(1,!_swdoc.boolInserting);
	Tab1.EnableTabItem(2,!_swdoc.boolInserting);
	Tab1.EnableTabItem(3,!_swdoc.boolInserting);
	Tab1.EnableTabItem(4,!_swdoc.boolInserting);
	Tab1.EnableTabItem(5,!_swdoc.boolInserting);

	tSLA.ShowTabItem(1,!_swdoc.boolInserting);
	tSLA.ShowTabItem(2,!_swdoc.boolInserting);
	tSLA.ShowTabItem(3,!_swdoc.boolInserting);
	tSLA.ShowTabItem(4,!_swdoc.boolInserting);
	tSLA.ShowTabItem(5,!_swdoc.boolInserting);

}

