							btn_del_pro1_OnPressed()
{
	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected reference record?";
	if(app.g.sl_deldbrow(sl_proposals1,sl_proposals1.curSel,'pk_conuri_id',null,strConfirm))
	{
			//-- loop through list and set seq for each to #
			var intID=0;
			var nextSeq=0;
			for(var x=0; x < sl_proposals1.rowCount();x++)
			{
				nextSeq=x+1;
				intID = sl_proposals1.GetItemTextRaw(x, "pk_conuri_id");

				var p={};
				p.ni = nextSeq;
				p.pci = intID;
				app.g.submitsimplesql("itsm_contract.contract_uri.setnindex", p);
			}

			sl_proposals1.Refresh();
			sl_proposals1.SetRowSelected(0, true, false);
			sl_proposals1_OnItemSelected(0);
	}

}

