							btn_pro_up1_OnPressed()
{
	//-- NWJ : move uri seq up 

	//-- get id to move up
	var currRow = sl_proposals1.curSel;
	var intUpID = sl_proposals1.GetItemTextRaw(sl_proposals1.curSel, 'pk_conuri_id');
	var intUpSeq = sl_proposals1.GetItemTextRaw(sl_proposals1.curSel, 'nindex');	

	//-- get id to down
	var intDownID = sl_proposals1.GetItemTextRaw(sl_proposals1.curSel-1, 'pk_conuri_id');
	var intDownSeq = sl_proposals1.GetItemTextRaw(sl_proposals1.curSel-1, 'nindex');

	var p={};
	p.ni = intDownSeq;
	p.pci = intUpID;
	if(app.g.submitsimplesql("itsm_contract.contract_uri.setnindex", p))
	{
		var p={};
		p.ni = intUpSeq;
		p.pci = intDownID;
		if(app.g.submitsimplesql("itsm_contract.contract_uri.setnindex", p))
		{
			//-- refresh list
			sl_proposals1.Refresh();
			sl_proposals1.SetRowSelected(currRow-1, true, false);
		}
	}

}

