							mbtn_ci_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	


	if(nItemID == iAssignCI)
	{
		var strURL = "fk_company_id=";// + oCust().fk_company_id;
		app.cmdb.search_ci(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
 			var arrayCIkeys = sResults.selectedkeys.split(",");
			var arrayCItext = sResults.selectedtext.split(",");	
			for (var x=0;x<arrayCIkeys.length;x++)
    		{
       			var pk_ci_id = arrayCIkeys[x];
        		var ci_item_text = arrayCItext[x];

				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_results1,pk_ci_id,0);
				if (rowPos == -1)
				{
           			var strRel = "Uses";
					app.cmdb.generate_meconfigrelation(contract.fk_cmdb_id,pk_ci_id,strRel);  

					//-- Create contract_pricing entry
					var cirec = app.g.get_record("config_itemi", pk_ci_id);
					if(cirec.ck_config_type!="")
					{
						var cityperec = app.g.get_record("config_typei", cirec.ck_config_type);
						if(cityperec)
						{
							app.g.add_contract_pricing(_swdoc.oContract().pk_contract_id,pk_ci_id,ci_item_text,'Configuration Item',cityperec.bus_price);
							_swdoc.boolRefreshPricing	= true;

							var strText = "Configuration Item "+ci_item_text+" has been added to this contract.";
							var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
							app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
							sl_contract_diary1.Refresh();
						}
					}
				}
			}
			app.g.sqllist_appendfilter(sl_results1, sResults.selectedkeys, "pk_auto_id");
			_swdoc.MF().sl_results1.Refresh();

			return;
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove customer and ci relation
		var strCIids = app.g.sl_selectedkeys(sl_results1,'pk_auto_id',true,",");
		var strCItext = app.g.sl_selectedkeys(sl_results1,'ck_config_item',true,",");
		if(strCIids=="")  return;

		if(!confirm("Are you sure you want to dis-associate the selected Configuration Items(s) from this Contract?"))return;

 		var arrayCIkeys = strCIids.split(",");
 		var arrayCINames = strCItext.split(",");
 		for (var x=0;x<arrayCIkeys.length;x++)
    	{
       		var pk_ci_id = arrayCIkeys[x];
       		var ci_item_text = arrayCINames[x];

			app.cmdb.delete_meconfigrelation(contract.fk_cmdb_id,pk_ci_id);
			app.g.delete_contract_pricing(_swdoc.oContract().pk_contract_id, pk_ci_id);
			_swdoc.boolRefreshPricing	= true;
			var strText = "Configuration Item "+ci_item_text+" has been removed from this contract.";
			var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
			app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
			_swdoc.MF().sl_contract_diary1.Refresh();
		}
		app.g.sqllist_clearselected(sl_results2);
		_swdoc.refresh_contract_cis();
		_swdoc.MF().sl_results1.Refresh();
	}

	//-- Update Pricing Summary
	app.g.update_contract_pricing_summary(_swdoc.oContract().pk_contract_id);
	_swdoc.UpdateFormFromData();
	_slf(_swdoc.MF().sl_pricing,"_swc_fk_contract_id="+pfu(_swdoc.oContract().pk_contract_id));



}

							mbtn_view_ci1_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;

   	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_results1,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_results1,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}


}

