		// ---- SG END

	}
	else if(nTab == tAudit)
	{
		_swdoc.MF().sl_contract_diary1.Refresh();

	}
	else if(nTab == tAssocaited)
	{
		_ete(mainform.fld_rt, sl_reqs.rowCount());
		var intTotalCost = app.g.sqllist_gettotalvalue(mainform.sl_reqs, "support_unit_cost");
		_ete(mainform.fld_tsu, intTotalCost);	
		_swdoc.MF().sl_reqs.Refresh();
	}

