							mbtn_service_actions_OnMenuItem(strName, nItemID)
{
	var iAssignService = 1;
	var iRemoveService = 2;	
	var iViewAssoc = 3;


	if(nItemID == iAssignService)
	{
		var strURL = "";
		app.cmdb.search_for_service(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			var arrayCIkeys = sResults.selectedkeys.split(",");
			var arrayCItext = sResults.selectedtext.split(",");
			var strText = "";
			var strAssociated = "";	
			for (var x=0;x<arrayCIkeys.length;x++)
    		{
        		var pk_ci_id = arrayCIkeys[x];
        		var ci_item_text = arrayCItext[x];

				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_results2,pk_ci_id,1);
				if (rowPos == -1)
				{
            		var strRel = "is a contract for";
					app.cmdb.generate_meconfigrelation(contract.fk_cmdb_id,pk_ci_id,strRel);  

					var subprice = "0.00";

                	var p = {};
                	p.key = _swdoc.oContract().fk_company_id;
                	p.sid = pk_ci_id;
                	var servicerec = app.g.get_sf_record("sc_subscription","itsm_contract.get_service_subscription",p);
					if(servicerec)
					{
						subprice = servicerec.subscription_price
					}
					app.g.add_contract_pricing(_swdoc.oContract().pk_contract_id,pk_ci_id,ci_item_text,'Service',subprice);
					if(strText!="")
						strText += "\n";
					strText += "Service "+ci_item_text+" has been added to this contract.";
					_swdoc.boolRefreshPricing	= true;
					if(strAssociated!="")
						strAssociated += ",";
					strAssociated += pk_ci_id;
				}
			}
			if(strAssociated!="")
			{

				var arrayNewKeys = strAssociated.split(",");
				if(arrayNewKeys.length<arrayCIkeys.length)
		   			MessageBox('One or more services are already associated with contract.');

				if(arrayNewKeys.length==1)
				{
					var p = {};
					p.cid = pk_ci_id;
					p.pid = contract.fk_cmdb_id;
					var rec = app.g.get_sf_record("config_reli","itsm_contract.get_relationship",p);
					if(rec)
					{
						//###rec.GetValue(0) fix
	    				app.OpenFormForEdit("contract_editrelationship", rec.pk_auto_id, "", true, function(boolRefresh)
	    				{
	    					if(boolRefresh)
	    						_swdoc.MF().sl_results2.Refresh();
	    				});
    				}
    			}
    			else
    			{
    				MessageBox('Association successful. Please edit the individual relationships to set Default Support Units.');
    			}
				var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
				app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
				sl_contract_diary1.Refresh();

				app.g.sqllist_appendfilter(sl_results2, sResults.selectedkeys, 1);
				sl_results2.Refresh();
   			}
   			else
   			{
	   			MessageBox('One or more services are already associated with contract.');
   			}
			return;	
		});
    }
	else if(nItemID == iRemoveService)
	{
		var strKeys = app.g.sqllist_selectedkeys(sl_results2, "pk_auto_id", true, ",");
		if(strKeys!="")
		{
			var strConfirm = "Are you sure you want to remove the selected Services from this Contract?";
			if(confirm(strConfirm))
			{
				var strText = "";
				var arrayKeys = strKeys.split(",");
				for (var x=0;x<arrayKeys.length;x++)
    			{
    				var intKey = arrayKeys[x];
					var nRow = app.g.sqllist_findrow_byvalue(sl_results2, intKey, 'pk_auto_id');
					var strServiceName = app.g.sl_getcolvalue(sl_results2, nRow, "description");

					app.cmdb.delete_meconfigrelation(contract.fk_cmdb_id,intKey);
					app.g.delete_contract_pricing(_swdoc.oContract().pk_contract_id, intKey);

					if(strText!="")
						strText +="\n";
					strText += "Service "+strServiceName+" has been removed from this contract.";

					_swdoc.boolRefreshPricing	= true;
					app.g.sqllist_delrow(sl_results2, nRow);
				}
				if(strText!="")
				{
					var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
					app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
					sl_contract_diary1.Refresh();
				}
				var strRemainingKeys = app.g.sqllist_get_valuesdel(sl_results2, 'pk_auto_id', ",", "");
				if(strRemainingKeys=="")
					strRemainingKeys = "-1";
				app.g.sqllist_filter(sl_results2, "pids="+strRemainingKeys);
	    		_ete(_swdoc.MF().fld_support_units, "No Service Selected");
	    		_ecol(_swdoc.MF().fld_support_units, "black");
			}
		}
	}
	else if(nItemID == iViewAssoc)
	{
		var intRelKey = app.g.sl_getcolvalue(sl_results2, sl_results2.curSel, "pk_auto_id");
		if(intRelKey=="")
			return false;

		var p = {};
		p.cid = intRelKey;
		p.pid = contract.fk_cmdb_id;
		var rec = app.g.get_sf_record("config_reli","itsm_contract.get_relationship",p);
		if(rec)
		{
			//###rec.GetValue(0) fix
	    	app.OpenFormForEdit("contract_editrelationship", rec.pk_auto_id, "", true);
    	}
	}

	//-- Update Pricing Summary
	app.g.update_contract_pricing_summary(_swdoc.oContract().pk_contract_id);
	_swdoc.UpdateFormFromData();
	_slf(_swdoc.MF().sl_pricing,"_swc_fk_contract_id="+pfu(_swdoc.oContract().pk_contract_id));



}

