							btn_view_act2_OnPressed()
{
		//-- open for edit
		var intURID = sl_blackout1.GetItemTextRaw(sl_blackout1.curSel, 'pk_id');				
		if(intURID>0)	
		{
			app.OpenFormForEdit("contract_review", intURID, "", true,function(){
				sl_blackout1.Refresh();			
				sl_blackout1.SetRowSelected(0);		

				//-- Refresh Next review date - lookup needed as updateformfromdata does not pick up new value
				var rec = app.g.get_record("contract", contract.pk_contract_id);
				contract.nextreviewx = app.g.fix_epoch(rec.nextreviewx);
				_swdoc.UpdateFormFromData();	
			});		
		}

}

							btn_del_act2_OnPressed()
{
	//-- ask to delete
	var strConfirm = "Are you sure you want to delete the selected review record?";
	if(app.g.sl_deldbrow(sl_blackout1,sl_blackout1.curSel,'pk_id',null,strConfirm))
	{	
		sl_blackout1.Refresh();
		sl_blackout1.SetRowSelected(0, true, false);

		//-- Refresh Next review date - lookup needed as updateformfromdata does not pick up new value
		var rec = app.g.get_record("contract", contract.pk_contract_id);
		contract.nextreviewx = app.g.fix_epoch(rec.nextreviewx);
		_swdoc.UpdateFormFromData();
	}
}

