							mbtn_sla_actions1_OnMenuItem(strName, nItemID)
{
	var iAddSLA = 1;
	var iDeleteSLA = 2;

	var funcUpdatePricingSummary = function()
	{
		//-- Update Pricing Summary
		app.g.update_contract_pricing_summary(_swdoc.oContract().pk_contract_id);
		_swdoc.UpdateFormFromData();
		_slf(_swdoc.MF().sl_pricing,"_swc_fk_contract_id="+pfu(_swdoc.oContract().pk_contract_id));
	}

	//-- Add Contract
	if(nItemID == iAddSLA)
	{
		if(!oSettings.get_setting("boolFilterAppcodeSLALoaded"))
			app.g.load_appcode_sla();
		var strFilter = "";
		var strAppcodes = oSettings.get_setting("strFilterAppcodeSLAContract");
		if(strAppcodes!="")
			strFilter = "appcode in ("+strAppcodes+")";
		var strURL = "mode=select&filter="+pfu(strFilter);
		app.OpenForm("browse_slas", strURL, true, function(x)
		{
			var strKeys = x.document.strSelectedKey;
			app.global.DebugPrint(strKeys);
			if(strKeys=="")return;
			var arrKeys = strKeys.split(",");
			for (var x=0; x< arrKeys.length;x++)
			{
				var fk_key = arrKeys[x];
				if(app.g.sl_findrow_byvalue(sl_slas1,fk_key,"pk_slad_id")==-1)
				{
            		var strRel = "Uses";
            		var recSLA = app.g.get_record("itsmsp_slad", fk_key);                               
					app.cmdb.generate_meconfigrelation(_swdoc.oContract().fk_cmdb_id,recSLA.fk_cmdb_id,strRel);  		

					app.g.add_contract_pricing(_swdoc.oContract().pk_contract_id,recSLA.fk_cmdb_id,recSLA.slad_id,'SLA',recSLA.price);
					_swdoc.boolRefreshPricing	= true;

					var strText = "SLA "+recSLA.slad_id+" has been added to this contract.";
					var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
					app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
					sl_contract_diary1.Refresh();
				}

			}
			app.global.DebugPrint("oContract().pk_contract_id = " + _swdoc.oContract().pk_contract_id);	
			var strSLAs = app.g.get_contract_slas(_swdoc.oContract().pk_contract_id);
			var strFilter = "swc=pk_slad_id&kvs="+strSLAs;
			app.g.apply_appcode_sla_filter(_swdoc.MF().sl_slas1, 'SLA-CONTRACT',strFilter);

			app.global.DebugPrint("strFilter = " + strFilter);	
			sl_slas1.Refresh();
			sl_slas1.SetRowSelected(0);
			sl_slas1_OnItemSelected(0);
			funcUpdatePricingSummary();
		});
	}
	else if(nItemID == iDeleteSLA)
	{
		//-- 15.12.2004
		//-- NWJ
		//-- delete contract
		var strSLAID = app.g.sl_getrawcolvalue(sl_slas1,-1,"pk_slad_id");
		if (strSLAID!="")
		{

			if (!confirm("Are you sure you want to dis-associate the selected SLA?")) return;
           	var recSLA = app.g.get_record("itsmsp_slad", strSLAID);                               

			var strSLACMDBID = app.g.sl_getcolvalue(sl_slas1, -1, "fk_cmdb_id");
			app.cmdb.delete_meconfigrelation(_swdoc.oContract().fk_cmdb_id,strSLACMDBID);
			app.g.delete_contract_pricing(_swdoc.oContract().pk_contract_id, strSLACMDBID);
			_swdoc.boolRefreshPricing	= true;
			var strText = "SLA "+recSLA.slad_id+" has been removed from this contract.";
			var strParams = "contract="+pfu(contract.pk_contract_id)+"&txt="+pfu(strText);
			app.g.submitsqs("form/tfcontract/add_diary_entry", strParams);
			sl_contract_diary1.Refresh();
			var strSLAs = app.g.get_contract_slas(_swdoc.oContract().pk_contract_id);
			var strFilter = "swc=pk_slad_id&kvs="+strSLAs;
			app.g.apply_appcode_sla_filter(_swdoc.MF().sl_slas1, 'SLA-CONTRACT',strFilter);

			sl_slas1.Refresh();
			sl_slas1.SetRowSelected(0);
			sl_slas1_OnItemSelected(0);

		}
		funcUpdatePricingSummary();
	}





}

