							fld_dec_method_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
	if(strValue == 1)
	{
		//### - use default
		_een(_swdoc.MF().lbl_def_units,true);
		_een(_swdoc.MF().fld_dec_units,true);
		_ema(_swdoc.MF().fld_dec_units,true);
	}
	else if(strValue ==2)
	{
		//### - prompt analyst
		_een(_swdoc.MF().lbl_def_units,false);
		_een(_swdoc.MF().fld_dec_units,false);
		_ema(_swdoc.MF().fld_dec_units,false);

		//###20120801
		if (0 != _swdoc.oContract().support_unit_dec_amount){
			_swdoc.oContract().support_unit_dec_amount = 0;
			_swdoc.UpdateFormFromData();
		}
	}
	else
	{
		//### - take from contract/service association
		_een(_swdoc.MF().lbl_def_units,true);
		_een(_swdoc.MF().fld_dec_units,true);
		_ema(_swdoc.MF().fld_dec_units,false);
	}
	//--SG 24/07/12 F0103808
	_evi(_swdoc.MF().fld_comments,true)
	_evi(_swdoc.MF().lbl_comments,true)

}

