function setup_history()
{
	//RJC 05.01.09
	//ITSM 2.4 FS 13.7
	//Create array of insert details
	var arrInserts = new Array();

	//-- get matching contract record
	var oLiveContractRec = app.g.get_record("itsmsp_slad", itsmsp_slad.pk_slad_id);
	var liveVal="";
	//-- for each field check if diff from contract
	for (x=0; x < dd.tables['itsmsp_slad'].columns.length;x++)
	{

		var colName = dd.tables['itsmsp_slad'].columns[x].Name;

		if(colName=="contract_notes")continue;

		//make sure the field exists
		if(oLiveContractRec[colName]!=undefined)
		{
			//MessageBox(colName + " : " + FORM_VALUE + " : " + oLiveContractRec[colName]);
			//-- if values differ and value is not empty put into a field table
			//-- if numeric value and = 0 then ignore
			var FORM_VALUE = itsmsp_slad[colName];
			if((app.g.dd_isnumeric('itsmsp_slad',colName))&&(FORM_VALUE==0))continue;

			var	dispFormVal = FORM_VALUE;
			var liveVal = oLiveContractRec[colName];
			var dispLiveVal = liveVal;
			if(app.g.dd_isnumeric('itsmsp_slad',colName))
			{
				if(FORM_VALUE<0)
				{
					FORM_VALUE= app.g.fix_epoch(FORM_VALUE);
					dispFormVal = app.g.convert_epochddmmyyyy(FORM_VALUE);
					dispLiveVal = app.g.convert_epochddmmyyyy(liveVal);
				}
			}

			if((liveVal!=FORM_VALUE))
			{
				arrInserts.push(new Array( app.g.dd_fieldlabel('itsmsp_slad',colName),app.g.pfs(dispLiveVal) , app.g.pfs(dispFormVal)))
			}
		}	
	}

	_swdoc.arrDiary = arrInserts;
}

