mbtn_ci_actions1_OnMenuItem(strName, nItemID)
{
	//-- F0091281
	if(!_swdoc.check_edit_rights())
		return false;

	var iAssignCI = 1;
	var iRemoveCI = 2;	
	var iRemoteSupport = 3;

	if(nItemID == iAssignCI)
