				//-- check that CI is not already in the list
				rowPos = app.g.sqllist_findrow_byvalue(sl_results1,pk_ci_id,0);
				if (rowPos == -1)
				{
           			var strRel = "Uses";
					app.cmdb.generate_meconfigrelation(itsmsp_slad.fk_cmdb_id,pk_ci_id,strRel);
				}
			}
			if(confirm("Would you like to set this SLA as the default SLA for these CI(s)?"))
