mbtn_slr_actions_OnMenuItem(strName, nItemID)
{
    var iAssociate = 1;
	var iDisassociate = 2;

	if(nItemID == iAssociate)
	{
		if(!oSettings.get_setting("boolFilterAppcodeSLALoaded"))
			app.g.load_appcode_sla();
		var strFilter = "";
		var strAppcodes = oSettings.get_setting("strFilterAppcodeSLR");
		if(strAppcodes!="")
			strFilter = "appcode in ('"+strAppcodes+"')";
		var strURL = "mode=select&filter="+pfu(strFilter);
		app.OpenForm("browse_slrs", strURL, true, function(x)
		{
			var intSlrId = x.document.strSelectedKey;
			if(intSlrId=="")return;

			//-- check that sla is not already in the list
			rowPos = app.g.sqllist_findrow_byvalue(_swdoc.MF().sl_slr,intSlrId,"pk_slr_id");
			if (rowPos == -1)
			{ 	
				var strRel = "Uses";
				app.cmdb.generate_meconfigrelation(_swdoc.oSLAD().fk_cmdb_id,intSlrId,strRel);  


				var strSLAs = app.g.get_sla_slrs(itsmsp_slad.fk_cmdb_id);
				var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
				// RJC F0104320
				app.g.apply_appcode_sla_filter(mainform.sl_slr,'SLA-SLR',strFilter);

				_swdoc.MF().sl_slr.Refresh();
				_swdoc.MF().sl_slr.SetRowSelected(0, true, false);
			}
		});
	}
	else if(nItemID == iDisassociate)
	{
		var intSlrId = app.g.sl_getrawcolvalue(_swdoc.MF().sl_slr,-1,"fk_cmdb_id");
		if(intSlrId=="")return;

		if (!confirm("Are you sure you want to dis-associate the selected SLR?")) return;

		app.cmdb.delete_meconfigrelation(_swdoc.oSLAD().fk_cmdb_id,intSlrId);
		var strSLAs = app.g.get_sla_slrs(itsmsp_slad.fk_cmdb_id);
		var strFilter = "swc=fk_cmdb_id&kvs="+strSLAs;
		// RJC F0104320
		app.g.apply_appcode_sla_filter(mainform.sl_slr,'SLA-SLR',strFilter);
		//-- Reload list
		_swdoc.MF().sl_slr.Refresh();

		//-- Handle enable/disable
		if (_swdoc.MF().sl_slr.rowCount()==0)
		{
			_een(_swdoc.MF().btn_slr_view, false);
		}
		else
		{
			_swdoc.MF().sl_slr.SetRowSelected(0, true, false);
		}
	}


}

