function UpdateReportTabSetting(intTabId,strValue)
{
	//-- DJH - Set visibility of specified report tab on swtoday
	var p = {};
	p.tid = intTabId;
	p.val = strValue
	app.g.submitsimplesql("manage_general_settings.updatereporttabsetting", p);
}

