							TabSettings_OnTabSelected(nTab)
{
	var tAssessment = 0;
	var tMatrix = 1;
	var tList = 2;
	var tImpact = 3;
	var tTime = 4;
	var tSettings = 5;
	var tRemote = 6;
	var nTabImpactLevelAssessment = 7;
	var tSelfservice = 8;

	if (nTab == nTabImpactLevelAssessment)
	{
		// set the relevant filter for each of the SQL lists based on the default value for the Usage
		switch (_swdoc.MF().pl_assessment_usage.value) 
		{
			case "Release Risk" :
				var strFilter = "_swc_usage_area=Release Risk";
			break;

			default : 
				var strFilter = "_swc_usage_area=Change Log Call Form";
			break;
		}
		_slf(_swdoc.MF().sl_ila_criteria,strFilter);
		_swdoc.MF().sl_ila_criteria.Refresh();
		_slf(_swdoc.MF().sl_ila_options,strFilter);
		_swdoc.MF().sl_ila_options.Refresh();
		_slf(_swdoc.MF().sl_ila_ranges,strFilter);
		_swdoc.MF().sl_ila_ranges.Refresh();
	}
	else if (nTab==tAssessment)
	{
		_swdoc.MF().sl_impact.Refresh();
		_swdoc.MF().sl_urgency.Refresh();
	}
	else if(nTab==tMatrix)
	{
		_swdoc.MF().sl_matrix.Refresh();
	}
	else if(nTab==tList)
	{
		_swdoc.MF().sl_sdv.Refresh();
	}
	else if(nTab==tImpact)
	{
		_swdoc.MF().sl_impact1.Refresh();
	}
	else if(nTab==tTime)
	{
		_swdoc.MF().sl_period.Refresh();
	}
	else if(nTab==tSettings)
	{
		_swdoc.MF().sl_settings.Refresh();
	}
	else if(nTab==tRemote)
	{
		_swdoc.MF().sl_remotesupport.Refresh();
	}
	else if(nTab==tSelfservice)
	{
		_een(_swdoc.MF().btn_del_wiz,false);
		_een(_swdoc.MF().btn_prop_wiz,false);
		_een(_swdoc.MF().btn_copy_wiz,false);
		_swdoc.MF().sl_wizards.Refresh();
	}

}

