							pl_settings_cat_OnValueChanged(strValue)
{
	var strFilter = "";

	if (strValue=="")
	{
		if (pl_dataset.value=="")
		{
			strFilter = "_swc_toplevelcategory=-1&_swc_appcode=-1";
		}
		else
		{
			strFilter = "_swc_appcode=" + pfu(pl_dataset.value);
		}
	}
	else
	{
		if (pl_dataset.value=="")
		{
			strFilter = "_swc_toplevelcategory=-1&_swc_appcode=-1";
		}
		else
		{
			strFilter = "_swc_toplevelcategory=" + pfu(strValue) + "&_swc_appcode=" + pfu(pl_dataset.value);
		}
	}
	_slf(sl_settings,strFilter);
	_swdoc.MF().sl_settings.Refresh();
}

