		MessageBox("Please select a Usage before creating a new Criterion");
		return true;
	}

	var strURL = "usage_area=" + pfu(_swdoc.str_assessment_usage) + "&editmode=Add";
    app.OpenFormForAdd("EfImpactCriterion", "", strURL, true,function(){
    	_swdoc.MF().sl_ila_criteria.Refresh();
    });

}

							btn_new_options_OnPressed()
