							btn_new_ranges_OnPressed()
{
	// TODO: Add your event handler code here
	if (_swdoc.str_assessment_usage == "")
	{
		MessageBox("Please select a Usage before creating a new Range");
		return true;
	}

	switch (_swdoc.str_assessment_usage)
	{
		case "Release Risk" :
			str_level_filter = "ASSESS-RELEASE-RISK";
		break;

		default :
			str_level_filter = "";
		break;
	}

	if ((_swdoc.str_assessment_usage != "Change Log Call Form") && (str_level_filter == ""))
	{
		MessageBox("There are no available Levels for the selected Usage. These should be configured, by a System Administrator, before continuing");
		return true;
	}

	var strURL = "usage_area=" + pfu(_swdoc.str_assessment_usage) + "&editmode=Add&levelfilter=" + pfu(str_level_filter);
    app.OpenFormForAdd("EfImpactRange", "", strURL, true, function(){
    	_swdoc.MF().sl_ila_ranges.Refresh();
    });

}

