							sl_settings_OnItemSelected(nRow)
{

	var intCurSel = _swdoc.MF().sl_settings.curSel;
	var boolSelected = false;
	if(intCurSel===0)
		boolSelected = true;
	else if(intCurSel)
		boolSelected = true;

	_een(btn_prop_setting, (boolSelected && pl_dataset.text!=""));
}

							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here
	TabSettings_OnTabSelected(0);
	var strDSFilterList = app.itsm.get_session_param("datasetFilterList");
	var strDS = app.itsm.get_session_param("dataset");

	var arrDSFilterList = strDSFilterList.split(",");

	_swdoc.MF().pl_dataset.pickList = "";
	for (i = 0; i < arrDSFilterList.length; i++)
	{ 
		if (_swdoc.MF().pl_dataset.pickList != "")
		{
			_swdoc.MF().pl_dataset.pickList += "|";
		}
		_swdoc.MF().pl_dataset.pickList += arrDSFilterList[i].replace(/'/g,"") + "^" + (i+1);
	}
	_ete(_swdoc.MF().pl_dataset , strDS);

	var strFilter = (strDS=="")?"":"_swc_appcode=" + pfu(strDS);
	_slf(sl_settings,strFilter);
	_swdoc.MF().sl_settings.Refresh();

	// -- WSSM Utility is not available in the Web Client
	if(app.bWebClient)
	{
		_evi(_swdoc.MF().btn_open_wiz_utility , false);
		_evi(_swdoc.MF().btn_refresh , false);
	}
	else
	{
		_evi(_swdoc.MF().btn_open_wiz_utility , app.session.isAdmin);
		_evi(_swdoc.MF().btn_refresh , app.session.isAdmin);
	}

}

