							btn_ssl_up_OnPressed()
{
	// TODO: Add your event handler code here

	//-- get selected vpme id to move up
	var currRow = sl_ssl.curSel;
	var intUpID = sl_ssl.GetItemTextRaw(sl_ssl.curSel, 'pk_id');
	var intUpSeq = sl_ssl.GetItemTextRaw(sl_ssl.curSel, 'view_order');	

	//-- get stage id to move down
	var intDownID = sl_ssl.GetItemTextRaw(sl_ssl.curSel-1, 'pk_id');
	var intDownSeq = sl_ssl.GetItemTextRaw(sl_ssl.curSel-1, 'view_order');

	var strParams = "vid="+pfu(intUpID);
	if(app.g.submitsqs("form/manage_general_settings/dec_wsslinks_seq", strParams))
	{
		var strParams = "vid="+pfu(intDownID);
		if(app.g.submitsqs("form/manage_general_settings/inc_wsslinks_seq", strParams))
		{
			//-- refresh list
			sl_ssl.Refresh();
			sl_ssl.SetRowSelected(currRow-1, true, false);
		}
	}

}

							btn_ssl_down_OnPressed()
{
	// TODO: Add your event handler code here
	//-- get selected vpme id to move down
	var currRow = sl_ssl.curSel;
	var intUpID = sl_ssl.GetItemTextRaw(sl_ssl.curSel+1, 'pk_id');
