	{
		var strParams = "vid="+pfu(intDownID);
		if(app.g.submitsqs("form/manage_general_settings/inc_wsslinks_seq", strParams))
		{
			//-- refresh list
			sl_ssl.Refresh();
			sl_ssl.SetRowSelected(currRow+1, true, false);
		}
	}

}

							btn_ssl_rem_OnPressed()
{
	// TODO: Add your event handler code here

	var strConfirm = "Are you sure you want to remove this Link?";
	var intSequence = sl_ssl.GetItemTextRaw(sl_ssl.curSel, "view_order");
	if(app.g.sl_deldbrow(sl_ssl,sl_ssl.curSel,'pk_id',null,strConfirm))
	{

		var strUpdate="";
		//-- loop through list and set seq for each - 1 to #
		var intID=0;
		var nextSeq=0;
		for(var x=0; x < sl_ssl.rowCount();x++)
		{

			var thisSeq = sl_ssl.GetItemTextRaw(x, "view_order");
			if(thisSeq>intSequence)
			{
				intID = sl_ssl.GetItemTextRaw(x, "pk_id");
				var strParams = "vid="+pfu(intID);
				app.g.submitsqs("form/manage_general_settings/dec_wsslinks_seq", strParams);
			}
		}

		sl_ssl.Refresh();
		sl_ssl.SetRowSelected(0);	
		sl_ssl_OnItemSelected(0);

		_een(btn_ssl_rem,(sl_ssl.rowCount()>0));

	}

}

							pl_assessment_usage_OnValueChanged(strValue)
{
	// TODO: Add your event handler code here
// F0111914 DTH: 20130619
	switch (strValue)
	{
		case "Change Impact":
			_swdoc.str_assessment_usage = "Change Log Call Form";
		break;

		case "Change / Release Risk":
			_swdoc.str_assessment_usage = "Release Risk";
		break;

		default:
			_swdoc.str_assessment_usage = "";
		break;
	}

//MessageBox(_swdoc.str_assessment_usage);

	// set the relevant filter for each of the SQL lists
	var strFilter = (_swdoc.str_assessment_usage=="")?"":"_swc_usage_area=" + pfu(_swdoc.str_assessment_usage);
	_slf(sl_ila_criteria,strFilter);
	_swdoc.MF().sl_ila_criteria.Refresh();
	_slf(sl_ila_options,strFilter);
	_swdoc.MF().sl_ila_options.Refresh();
	_slf(sl_ila_ranges,strFilter);
	_swdoc.MF().sl_ila_ranges.Refresh();

}

