							pl_dataset_OnValueChanged(strValue)
{
	var strDataFilter = "";

	if (strValue=="")
	{
		strDataFilter = "_swc_toplevelcategory=-1&_swc_appcode=-1";
	}
	else
	{
		if (_swdoc.MF().pl_settings_cat.value=="")
		{
			strDataFilter = "_swc_appcode=" + pfu(strValue);
		}
		else
		{
			strDataFilter = "_swc_toplevelcategory=" + pfu(_swdoc.MF().pl_settings_cat.value) + "&_swc_appcode=" + pfu(strValue);
		}
	}

	_slf(_swdoc.MF().sl_settings,strDataFilter);
	_swdoc.MF().sl_settings.Refresh();

	if (strValue=="")
	{
		_een(_swdoc.MF().btn_prop_setting,false);
	}
}

