							btn_notif_del_OnPressed()
{
	var strConfirm = "Are you sure you want to delete this Notification?";
	var intSequence = sl_notifications.GetItemTextRaw(sl_notifications.curSel, "sequence");
	if(app.g.sl_deldbrow(sl_notifications,sl_notifications.curSel,'pk_auto_id',null,strConfirm))
	{
		var strUpdate="";
		//-- loop through list and set seq for each - 1 to #
		var intID=0;
		var nextSeq=0;
		for(var x=0; x < sl_notifications.rowCount();x++)
		{

			var thisSeq = sl_notifications.GetItemTextRaw(x, "sequence");
			if(thisSeq>intSequence)
			{
				intID = sl_notifications.GetItemTextRaw(x, "pk_auto_id");
				var strParams = "id="+pfu(intID);
				app.g.submitsqs("form/manage_general_settings/dec_notifs_seq", strParams);
			}
		}


		sl_notifications.Refresh();
		sl_notifications.SetRowSelected(0);	

		_een(btn_notif_del,(sl_notifications.rowCount()>0));
	}
}

