							btn_notif_add_matrix_OnPressed()
{
	//-- NWJ - Create matrix entry - check it doesnt already exist though
	if ( (lb_impact.text=="") || (lb_urgency.text=="") || (lb_sla.text==""))
	{
		MessageBox("Please ensure you have selected all mandatory options for this matrix setting.");
		return;
	}

	//-- create key - check if it exists
	var strKey = lb_impact.text + ":" + lb_urgency.text;

	var p = {};	p.pk_matrix_key = strKey;
	var intCount = app.g.sqs_rowcountbycol(sl_matrix.table,p)
	if(intCount>0)
	{
		MessageBox("This priority(SLA) matrix setting has already been defined.");
		return;
	}

	var p={};
	p.fku = lb_urgency.text;
	p.fki = lb_impact.text;
	p.fks = lb_sla.text;
	p.pkm = strKey;
	if(app.g.submitsimplesql("manage_general_settings.itsm_slamatrix.insert", p))
	{
		app.g.sl_refresh(sl_matrix,null,0);
	}

}

