						var _swdoc = (IsObjectDefined("_bHtmlWebClient"))?top:_swdoc;

function MF()
{
	return _swdoc.mainform;
}

function oDataform()
{
	return sc_dataform;
}

							OnFormLoaded(bDataReloaded)
{
	// TODO: Add your event handler code here
	if(sc_dataform.fk_service_typei=="")_swdoc.oDataform().fk_service_typei = _swdoc.GetArg("fk_service_typei");

	if(_swdoc.oDataform().callclass == "" )
	{
		_swdoc.oDataform().callclass =_swdoc.GetArg("callclass");
	}
	else
	{
		_een(_swdoc.MF().lb_process1,true);
		_slf(_swdoc.MF().lb_process1,"_swc_callclass="+_swdoc.oDataform().callclass +"&_swc_flg_active=1");
	}
    _swdoc.UpdateFormFromData();
    _swdoc.ResetModiedFlag("sc_dataform");

}

							tb_suppowner1_OnDropDown()
{
	if(!app.service.can_update(true))
		return false;
	//-- pick analyst or group
	app.g.pick_analystorgroup("Assign To", function(oTmp)
	{
		if((oTmp)&&(oTmp.oGroup!=false))
		{
			//-- we have info
			_swdoc.oDataform().suppgroup = oTmp.oGroup.id;
			_swdoc.oDataform().owner = oTmp.oAnalyst.id;
			_ete(tb_suppgroup1,oTmp.oGroup.name);	
			_ete(tb_suppowner1,oTmp.oAnalyst.name);						
			_swdoc.UpdateFormFromData();
		}
		else
		{
			// value has been selected, but no support group (third party)
			if(oTmp)
			{
				MessageBox("Unable to log a request to a Third Party Supplier. Group/Analyst values have not been set.");
			}

		}
	});

	return false;
}

							tb_suppowner1_OnRightClick()
{
	if(!app.service.can_update(true))
		return false;
	oService().suppgroup = "";
	oService().owner = "";		

	_ete(tb_suppgroup1,"");
	_ete(tb_suppowner1,"");				

	return true;
}

							lb_profile1_OnDropDown()
{
	if(!app.service.can_update(true))
		return false;
	app.g.pick_problemprofile(_swdoc.oDataform().probcode,"",true, function(strInfo){
		if(strInfo!=""){
			var arrInfo = strInfo.split("|");
			_swdoc.oDataform().probcode=arrInfo[0];
			_swdoc.UpdateFormFromData();
		}

	});
	return false;
}

							lb_profile1_OnRightClick()
{
	if(!app.service.can_update(true))
		return false;
	_swdoc.oDataform().probcode="";
	_ete(lb_profile1,"");
	_swdoc.UpdateFormFromData();
	return true;
}

							fld_wizard_OnRightClick()
