							fld_wizard_OnLeftClick()
{
	// TODO: Add your event handler code here
	app.OpenForm("browse_wizards", "mode=select", true,function(x)
	{
	//MessageBox(x._swdoc.strSelectedKey);
	if(x._swdoc.strSelectedKey!="")
		_swdoc.oDataform().fk_wizard = x._swdoc.strSelectedKey;
	_swdoc.UpdateFormFromData();
	});
	return false;
}

							lb_process1_OnLeftClick()
{
	// TODO: Add your event handler code here
	if(_swdoc.oDataform().callclass=="")
		return false;
	return true;
	var strArgs = "mode=select&class="+_swdoc.oDataform().callclass;
	app.OpenForm("browse_bpm",strArgs , true, function(x)
	{
	if(x.strSelectedKey!="")
		_swdoc.oDataform().bpm_wf_id = x.strSelectedKey;
	_swdoc.UpdateFormFromData();
	});
	return false;
}

