//-- F0091281
function check_edit_rights()
{
	if(!app.session.HaveAppRight(ADMIN_SETTINGS, SLM_EDIT, app.session.dataDictionary))
	{
		app.global.MessageBox('Insufficient privileges to perform operation.\n\nYou have been denied the: [Can edit items in Service Level Management] permission.\n\nPlease contact your Supportworks system administrator', 16);
		return false;	
	}
	if(!app.session.HaveRight(ANALYST_RIGHT_C_GROUP,ANALYST_RIGHT_C_CANMODIFYSLA))
	{
		app.global.MessageBox('Insufficient privileges to perform operation.\n\nYou have been denied the: [Can modify SLAs] permission.\n\nPlease contact your Supportworks system administrator', 16);
		return false;	
	}
	return true;
}
function setup_fields(){


	//###20120618 - disable everything
	var blnSpecificSelected = false;
	var blnCallRecordSelected = (1 == _swdoc.EscRef().call_record);
	var blnMatchWithSelected = (1 == _swdoc.EscRef().match_with);
	var blnDiscoverSelected = (1 == _swdoc.EscRef().discover_data);
	if (!(blnCallRecordSelected || blnMatchWithSelected || blnDiscoverSelected)){
		blnSpecificSelected = true;
		if (1!=_swdoc.EscRef().flg_specific) _swdoc.EscRef().flg_specific = 1;
	}
	//Set specific email field to mandatory
	_een(_swdoc.MF().lb_specificemail, blnSpecificSelected);
	_ema(_swdoc.MF().lb_specificemail, blnSpecificSelected);
	if (!(blnSpecificSelected)){
		if (""!=_swdoc.EscRef().specific_email) _swdoc.EscRef().specific_email='';
	};


	//Set all other fields in TAB1 to disabled as they shouldnt be used
	_een(_swdoc.MF().lb_callrecordfield, !(blnSpecificSelected));
	_ema(_swdoc.MF().lb_callrecordfield, !(blnSpecificSelected));


	if (blnSpecificSelected){
		if (""!=_swdoc.EscRef().call_record_field) _swdoc.EscRef().call_record_field='';
	}


	var boolShowLeft = (blnDiscoverSelected || blnMatchWithSelected);
	_een(_swdoc.MF().lb_discover_leftdb, boolShowLeft); 
	_ema(_swdoc.MF().lb_discover_leftdb, boolShowLeft); 
	_een(_swdoc.MF().lb_discover_lefttable, boolShowLeft); 
	_ema(_swdoc.MF().lb_discover_lefttable, boolShowLeft); 
	_een(_swdoc.MF().lb_discover_leftfield, boolShowLeft); 
	_ema(_swdoc.MF().lb_discover_leftfield, boolShowLeft); 
	_een(_swdoc.MF().lb_discover_leftfieldtouse, boolShowLeft); 
	_ema(_swdoc.MF().lb_discover_leftfieldtouse, boolShowLeft);
	_een(_swdoc.MF().tb_cond, boolShowLeft);
	_een(_swdoc.MF().fld_multi_mail, boolShowLeft);

	if (!(boolShowLeft)){
		if (""!=_swdoc.EscRef().discover_data_lefttable) _swdoc.EscRef().discover_data_lefttable = '';
		if (""!=_swdoc.EscRef().discover_data_leftfield) _swdoc.EscRef().discover_data_leftfield = '';
		if (""!=_swdoc.EscRef().discover_data_leftfield_to_use) _swdoc.EscRef().discover_data_leftfield_to_use = '';
	}

	_een(_swdoc.MF().lb_discover_rightdb, blnDiscoverSelected); 
	_ema(_swdoc.MF().lb_discover_rightdb, blnDiscoverSelected); 
	_een(_swdoc.MF().lb_discover_righttable, blnDiscoverSelected); 
	_ema(_swdoc.MF().lb_discover_righttable, blnDiscoverSelected); 
	_een(_swdoc.MF().lb_discover_rightfield, blnDiscoverSelected); 
	_ema(_swdoc.MF().lb_discover_rightfield, blnDiscoverSelected); 
	_een(_swdoc.MF().lb_discover_rightfieldtouse, blnDiscoverSelected); 
	_ema(_swdoc.MF().lb_discover_rightfieldtouse, blnDiscoverSelected);

	_een(_swdoc.MF().tb_cond_right, blnDiscoverSelected && !(_swdoc.EscRef().discover_data_leftdb == _swdoc.EscRef().discover_data_rightdb));


	if (!(blnDiscoverSelected)){
		if (""!=_swdoc.EscRef().discover_data_righttable) _swdoc.EscRef().discover_data_righttable = '';
		if (""!=_swdoc.EscRef().discover_data_rightfield) _swdoc.EscRef().discover_data_rightfield = '';
		if (""!=_swdoc.EscRef().discover_data_rightfield_use) _swdoc.EscRef().discover_data_rightfield_use = '';
	}

	_swdoc.UpdateFormFromData();


}

