							OnFormLoaded(bDataReloaded)
{
	if(!app.session.HaveAppRight(ADMIN_SETTINGS, SLM_VIEW, app.session.dataDictionary)) 
	{
		MessageBox("You do not have permission to view SLM items.");
		_swdoc.CloseForm();
		return false;
	}

	//-- Load List of Opencall columns.
	var wo = "just to provide nonnull";
	_swdoc.SwDataTableList = app.g.dd_gettablelist(wo);

	if (boolNotificationFullCache){
		_swdoc.SwCacheTableList = app.g.cache_gettablelist();
	} else {
		_swdoc.SwCacheTableList = "swanalysts|swanalysts_groups|swgroups"
	}

	var strLbData = app.g.dd_getcolumndbnamelist("opencall", false,undefined,undefined,false);
	_swdoc.MF().lb_callrecordfield.pickList = strLbData;

	//-- Load List of swdata Tables

	var e_template = _swdoc.EscRef().email_template;
	if(""!=_swdoc.EscRef().email_mailbox){
		_swdoc.MF().lb_mailbox_OnValueChanged(_swdoc.EscRef().email_mailbox);
		_swdoc.EscRef().email_template = e_template;
	}


	if("syscache"==	_swdoc.EscRef().discover_data_leftdb.toLowerCase()){
		_swdoc.MF().lb_discover_lefttable.pickList = _swdoc.SwCacheTableList;
		if(""!=_swdoc.EscRef().discover_data_lefttable){
			strLbData = app.g.cache_getcolumnlisttblename(_swdoc.EscRef().discover_data_lefttable, false,undefined,false);
			_swdoc.MF().lb_discover_leftfield.pickList = strLbData;
			_swdoc.MF().lb_discover_leftfieldtouse.pickList = strLbData;
		}
	} else {
		_swdoc.MF().lb_discover_lefttable.pickList = _swdoc.SwDataTableList;
		if(""!=_swdoc.EscRef().discover_data_lefttable){
			strLbData = app.g.dd_getcolumndbnamelist(_swdoc.EscRef().discover_data_lefttable, true,undefined,undefined,false);
			_swdoc.MF().lb_discover_leftfield.pickList = strLbData;
			_swdoc.MF().lb_discover_leftfieldtouse.pickList = strLbData;
		}
	}


	if("syscache"==	_swdoc.EscRef().discover_data_rightdb.toLowerCase()){
		_swdoc.MF().lb_discover_righttable.pickList = _swdoc.SwCacheTableList;
		if(""!=_swdoc.EscRef().discover_data_righttable){
			strLbData = app.g.cache_getcolumnlisttblename(_swdoc.EscRef().discover_data_righttable, false,undefined,false);
			_swdoc.MF().lb_discover_rightfield.pickList = strLbData;
			_swdoc.MF().lb_discover_rightfieldtouse.pickList = strLbData;
		}
	} else {
		_swdoc.MF().lb_discover_righttable.pickList = _swdoc.SwDataTableList;
		if(""!=_swdoc.EscRef().discover_data_righttable){
			strLbData = app.g.dd_getcolumndbnamelist(_swdoc.EscRef().discover_data_righttable, true,undefined,undefined,false);
			_swdoc.MF().lb_discover_rightfield.pickList = strLbData;
			_swdoc.MF().lb_discover_rightfieldtouse.pickList = strLbData;
		}
	}

	_swdoc.setup_fields();

	_swdoc.ResetModiedFlag("escalation_reference");

	_evi(_swdoc.MF().btn_match_cols, !(app.bWebClient));

}

