							lb_discover_lefttable_OnValueChanged(strValue)
{

	if(strValue!=''){
		var strLbData = "";
		if ("syscache" != _swdoc.MF().lb_discover_leftdb.value.toLowerCase()){
			strLbData = app.g.dd_getcolumndbnamelist(strValue, true,undefined,undefined,false);
		} else {
			strLbData = app.g.cache_getcolumnlisttblename(strValue, false,undefined,false);
		}
		_swdoc.MF().lb_discover_leftfield.pickList = strLbData;
		_swdoc.MF().lb_discover_leftfieldtouse.pickList = strLbData;
		_swdoc.EscRef().discover_data_leftfield = "";
		_swdoc.EscRef().discover_data_leftfield_to_use = "";
		_swdoc.EscRef().extra_cond = "";
		_swdoc.UpdateFormFromData();
	}

}

