							lb_mailbox_OnLeftClick()
{
	//-- get mailbox list

	if(lb_mailbox.pickList=="")
	{
		//-- call php to get mailbox list
		var strURL = "php/itsm/get_mailboxlist.php"; 
		var strResult = app.g.submit_php(strURL,true);
		lb_mailbox.pickList = strResult;
	}


	return true;
}

							lb_discover_leftdb_OnValueChanged(strValue)
{

	if("syscache"==strValue.toLowerCase()){
		_swdoc.MF().lb_discover_lefttable.pickList = _swdoc.SwCacheTableList;
	} else {
		_swdoc.MF().lb_discover_lefttable.pickList = _swdoc.SwDataTableList;
	}
	_swdoc.MF().lb_discover_leftfield.pickList = "";
	_swdoc.MF().lb_discover_leftfieldtouse.pickList = "";
	_swdoc.EscRef().discover_data_lefttable = "";
	_swdoc.EscRef().discover_data_leftfield = "";
	_swdoc.EscRef().discover_data_leftfield_to_use = "";
	_swdoc.EscRef().extra_cond = "";
	_een(_swdoc.MF().tb_cond_right, ( 1 == _swdoc.EscRef().discover_data ) && !(strValue == _swdoc.EscRef().discover_data_rightdb));
	_swdoc.UpdateFormFromData();

}

