	_swdoc.UpdateFormFromData();


}

							btn_test_OnPressed()
{

var test_callref = prompt("Please provide a call reference");
if (test_callref && !(isNaN(test_callref))){

	var xmlmc = new XmlMethodCall;

	// Set up our input parameters
	xmlmc.SetValue("callRef", test_callref);
	xmlmc.SetValue("verb", "rolevpmetest");

	xmlmc.SetValue("email_mailbox", _swdoc.MF().lb_mailbox.value);
	xmlmc.SetValue("email_template", _swdoc.MF().lb_template.value);
	xmlmc.SetValue("failure_email", _swdoc.MF().fld_failure_email.value);
	xmlmc.SetValue("send_multi", _swdoc.MF().fld_multi_mail.value);

	xmlmc.SetValue("flg_specific", _swdoc.MF().fld_specific.value);
	xmlmc.SetValue("specific_email", _swdoc.MF().lb_specificemail.value);
	xmlmc.SetValue("match_condition_value", _swdoc.MF().fld_match_condition.value);

	xmlmc.SetValue("call_record", _swdoc.MF().chk_call_record.value);
	xmlmc.SetValue("call_record_field", _swdoc.MF().lb_callrecordfield.value);

	xmlmc.SetValue("match_with", _swdoc.MF().chk_match_with.value);
	xmlmc.SetValue("discover_data_leftdb", _swdoc.MF().lb_discover_leftdb.value);
	xmlmc.SetValue("discover_data_lefttable", _swdoc.MF().lb_discover_lefttable.value);
	xmlmc.SetValue("discover_data_leftfield", _swdoc.MF().lb_discover_leftfield.value);
	xmlmc.SetValue("discover_data_leftfield_to_use", _swdoc.MF().lb_discover_leftfieldtouse.value);
	xmlmc.SetValue("extra_cond", _swdoc.MF().tb_cond.value);

	xmlmc.SetValue("discover_data", _swdoc.MF().fld_discover.value);
	xmlmc.SetValue("discover_data_rightdb", _swdoc.MF().lb_discover_rightdb.value);
	xmlmc.SetValue("discover_data_righttable", _swdoc.MF().lb_discover_righttable.value);
	xmlmc.SetValue("discover_data_rightfield", _swdoc.MF().lb_discover_rightfield.value);
	xmlmc.SetValue("discover_data_rightfield_use", _swdoc.MF().lb_discover_rightfieldtouse.value);
	xmlmc.SetValue("right_cond", _swdoc.MF().tb_cond_right.value);

	// Invoke the method
	if(xmlmc.Invoke("VPME", "EscalationNotification"))
	{
		var res1 = xmlmc.GetReturnValue("res1");
		if (""==res1)
			MessageBox("No results found!");
		else
			MessageBox(res1);
	}
	else
	{
		MessageBox("Error:" + xmlmc.GetLastError());
	}

}

}

							lb_role_OnValueChanged(strValue)
{

	if (!(strValue.match(/^[\_\-a-zA-Z0-9]*$/))){
		MessageBox("Please use only alphanumeric, underscore or hyphen characters (no spaces or commas) in the role field");
		_swdoc.EscRef().role = strValue.replace(/[^\_\-a-zA-Z0-9]/g, "");
		_swdoc.UpdateFormFromData();
	}
	if (0==strValue.toLowerCase().indexOf("rolevpmetest")){
		MessageBox("Please use a different name for this role (what you are using is reserved)");
		_swdoc.EscRef().role = "";
		_swdoc.UpdateFormFromData();
	}

}

							btnfailanalyststemail_OnPressed()
{

	app.g.pick_analyst("Select Analyst Email Address", function(oAnalyst)
	{
		if(!oAnalyst)
			return false;
		_swdoc.EscRef().failure_email = oAnalyst.email;

		_swdoc.UpdateFormFromData();	

	});
}

							btnfailcustemail_OnPressed()
{

	var strURL = "canreset=1&";
	app.g.search_for("customers",true,strURL,function(oRes)
	{
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");
		var c = app.g.get_record("userdb", arrKeys[0]);
		if (c) _swdoc.EscRef().failure_email = c.email;
		_swdoc.UpdateFormFromData();	
	});
}

							btnspecanalyststemail_OnPressed()
