OnFormLoaded(bDataReloaded)
{
	// DTH: F0097669 KB Cat Rights	
	_swdoc.arrKbCatRights = app.g.get_anlyst_kbcat_rights(app.session.analystId, _swdoc.swkb().catalog);
	arrKbAnalystCats = app.g.get_authorised_catalog_names(app.session.analystId);

	if(_swdoc.swkb().docref=="")
	{
		if(app.bWebClient)
		{
			var strMessage = 'Webclient does not support creating External Documents for the ITSM application.\n';
			strMessage += 'Please use the full client to access this functionality.'
			MessageBox(strMessage);
			_swdoc.CloseForm();
			return true;
		}
		//-- inserting
		_swdoc.boolInserting = true;

		//-- set default values
		//F0086382
		var intCurrentTime = app.global.GetCurrentEpocTime(); //-- date
		_swdoc.swkb().docdatex = "" + intCurrentTime;

		_swdoc.swkb().owneranalystid = app.session.analystId;     //-- owner
		_swdoc.swkb().ownergroupid = app.session.groupId;
		_swdoc.swkb().docflags = "1"; //- -visible to customers
		_swdoc.swkb().flg_indexed = "1"; //- -visible to customers

		_ema(s_kb__document,true);
		_een(s_kb__document,true);

		//-- set default process settings		
		var oRec = app.g.get_record("swkb_catalogs", _swdoc.swkb().catalog);
		if(oRec)
		{
			_swdoc.swkb().fk_bpm_process = oRec.fk_bpm_process;
			_swdoc.swkb().fk_bpm_assignid = oRec.fk_bpm_assignid;
			_swdoc.swkb().fk_bpm_assigngrp = oRec.fk_bpm_assigngrp;
			_swdoc.swkb().fk_bpm_priority = oRec.fk_bpm_priority;
			_swdoc.swkb().fk_bpm_sla = oRec.fk_bpm_sla;
			_swdoc.UpdateFormFromData();	
		}

		var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
		if(strItems.indexOf("External Document")<0)
		{
			MessageBox('You do not have permission to create documents in the External Documents Catalog.');
			_swdoc.CloseForm();
		}
		_swdoc.swkb().catalogname = "External Documents";
		_swdoc.MF().s_kb__catalogid.pickList = strItems;

		_swdoc.ResetModiedFlag("swkb_articles");


	}
	else
	{
		_swdoc.SetTitle("Manage KnowledgeBase Document");
		_swdoc.boolInserting = false;

		for(var x=0; x< mainform.elements.length;x++)
		{
			var oE = mainform.elements[x];
			if(oE.dataRef!="" && ((oE.type == FE_FIELD)||(oE.type == FE_SEARCHFIELD)))
			{
				var arrI = oE.dataRef.split(".");
				var strFormattedValue = dd.tables[arrI[0]].columns[arrI[1]].FormatValue(oE.text);

				_swdoc.array_changes[oE.dataRef] = new Object();
				_swdoc.array_changes[oE.dataRef].name = dd.tables[arrI[0]].columns[arrI[1]].DisplayName;
				_eva(_swdoc.array_changes[oE.dataRef] , strFormattedValue);
				_swdoc.array_changes[oE.dataRef].newvalue = "";
			}
		}

		_ema(s_kb__document,false);
		_een(s_kb__document,false);
		if(swkb_articles.sourcepath!="")
			_een(btn_ext,true);

		if (!in_array(arrKbAnalystCats, _swdoc.swkb().catalogname)) // Analyst does not have this catalog in his / her list based on rights
		{
			if(arrKbAnalystCats[_swdoc.swkb().catalog]==undefined)
			{
				_een(_swdoc.MF().s_kb__catalogid, false);
				_evi(_swdoc.MF().s_kb__catalogid, false);
				_evi(_swdoc.MF().eb_catalog_name, true);
				_ete(_swdoc.MF().eb_catalog_name, _swdoc.swkb().catalogname);
			}
			else
			{
				_swdoc.swkb().catalogname = arrKbAnalystCats[_swdoc.swkb().catalog];
				// _swdoc.get_catalog_list();
				// above line commented out as part of 106478.
				// START F106478
				var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
				_swdoc.MF().s_kb__catalogid.pickList = strItems;
				// END F106478
				_swdoc.ResetModiedFlag("swkb_articles");
				//MF().s_kb__catalogid.pickList = arrKbAnalystCats.join("|");
			}
		}
		else
		{
			//-- load catalog list - before data is loaded
			// _swdoc.get_catalog_list();
			// above line commented out as part of 106478.
			// START F106478
			var strItems = app.g.get_anlyst_kbcats_foradd(app.session.analystId);
			_swdoc.MF().s_kb__catalogid.pickList = strItems;
			// END F106478
		}
	}

	if(swkb_articles.callref!="")
		_een(btn_call, true);

	//-- RJC - F0105435
	app.g.apply_appcode_bpm_filter(_swdoc.MF().s_kb__fk_bpm_process, 'BPM');
	//-- EOF - F0105435

	TabInfo.ShowTabItem(1,!_swdoc.boolInserting);
	TabInfo.ShowTabItem(2,!_swdoc.boolInserting);
