function init_form()
{
	//-- 27.10.2004
	//-- fix date fields as getrecord does not process them correctly
	if (_swdoc.oContract().pk_contract_id !="")
	{	
		_swdoc.boolInserting=false;
		_swdoc.MF().TabContract.ShowTabItem(1,true);		
		_swdoc.MF().TabContract.ShowTabItem(2,true);		
		_swdoc.MF().TabContract.ShowTabItem(3,true);		
		_swdoc.check_contract_state(true);
		_swdoc.refresh_contract_cis();	
		_swdoc.load_sla_details();	
		if(contract.flg_sendemail==1)
		{
			_een(mainform.sndemail, true);
			_een(mainform.lb_mailbox1, true);
			_een(mainform.lb_template1, true);
		}
		_een(_swdoc.MF().Button1,true);
		_een(_swdoc.MF().Button2,true);
	}
	else
	{	
		//-- inserting
		_swdoc.boolInserting = true;
		_swdoc.oContract().fk_company_id= unescape(_swdoc.GetArg("fk_company_id"));
		_swdoc.oContract().type = unescape(_swdoc.GetArg("type"));
		_swdoc.MF().TabContract.ShowTabItem(1,false);		
		_swdoc.MF().TabContract.ShowTabItem(2,false);		
		_swdoc.MF().TabContract.ShowTabItem(3,false);		
		contract.timezone = app.session.timeZone;
		_een(_swdoc.MF().mbtn_view_sla, false);
		_een(_swdoc.MF().btn_add_act2, false);
		_swdoc.UpdateFormFromData();
	}	

	_swdoc.ResetModiedFlag('contract');
}


//-- check contract state
function check_contract_state(boolSave)
{	
	if(boolSave==undefined)boolSave = true;
	//-- if the contract end date is less than todays date then set flag to 0
	//-- or if the start date is greater than todays date (not started yet)
	if ( (app.g.fix_epoch(_swdoc.oContract().end_datex) < app.global.GetCurrentEpocTime()) || (app.g.fix_epoch(_swdoc.oContract().start_datex) > app.global.GetCurrentEpocTime())	)			
	{
		//-- if system has not flagged it yet set flag and save
		if (_swdoc.oContract().flg_valid=="1")
		{	
			_swdoc.oContract().flg_valid="0";
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
	else
	{
		if (_swdoc.oContract().flg_valid==0)
		{	
			_swdoc.oContract().flg_valid="1";
			_swdoc.oContract().flg_ewemail_sent="0";			
			_swdoc.oContract().flg_expemail_sent="0";						
			_swdoc.UpdateFormFromData();
			if(boolSave)_swdoc.Save(0);
		}
	}
}

//-- 26.02.2008 - nwj
//-- refresh contract ci list
function refresh_contract_cis()
{
	var strCMDBids = app.cfg.get_contract_cikeys(_swdoc.oContract().pk_contract_id,true);
	_slf(_swdoc.MF().sl_contractci, "pids=" + strCMDBids + "&sf=cilist_activebln");
	_swdoc.MF().sl_contractci.SetRowSelected(0);
	_swdoc.MF().sl_contractci.Refresh();
}

function load_sla_details()
{
	var strParams = "cdb=1&table=system_sla&okc=&kv="+pfu(contract.fk_priority)
	var oRS = app.g.get_sqrecordset("select.tablebykey",strParams); 
	if(oRS.Fetch())
	{
		_swdoc.boolLoading = true;
		contract.timezone = app.g.get_field(oRS,"timezone");
		contract.tpoptions = app.g.get_field(oRS,"tpoptions");
		contract.start_datex = app.g.fix_epoch(app.g.get_field(oRS,"tpvalidfrom"));
		contract.end_datex =  app.g.fix_epoch(app.g.get_field(oRS,"tpexpireend"));
		_swdoc.respTime = app.g.get_field(oRS,"resptime");

		var lclRespTime = _swdoc.respTime/60;
		var lclRespMins = lclRespTime % 60;
		var lclRespHrs = (lclRespTime - lclRespMins) / 60;
		//-- F0099429
		contract.resphrs = lclRespHrs;
		contract.respmins = lclRespMins;

		_swdoc.fixTime = app.g.get_field(oRS,"fixtime");
		var lclFixTime = _swdoc.fixTime/60;
		var lclFixMins = lclFixTime % 60;
		var lclFixHrs = (lclFixTime - lclFixMins) / 60;
		//-- F0099429
		contract.fixhrs = lclFixHrs;
		contract.fixmins = lclFixMins;
		contract.contract_notes = app.g.get_field(oRS,"tpnotes");

		contract.fk_manager = app.g.get_field(oRS,"tpcontactname");
		contract.owner_tel = app.g.get_field(oRS,"tpcontacttel");
		contract.owner_email = app.g.get_field(oRS,"tpcontactemail");

		_swdoc.UpdateFormFromData();
		_swdoc.Save(0); 
		_swdoc.boolLoading = false;
	}	
}

						OnDataSaved()
{


    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		
    	//-- need to reload site record to get fk_cmdb_id
    	_swdoc.LoadRecordData("swdata", "contract", _swdoc.oContract().pk_contract_id);

    	var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgContractRelationship");
    	var oSupplier = app.g.get_record("SUPPLIER", _swdoc.oContract().fk_company_id);
    	app.cmdb.generate_meconfigrelation(oSupplier.fk_cmdb_id,_swdoc.oContract().fk_cmdb_id,strRel);  

		_swdoc.boolInserting=false;
	}


	//RJC 05.01.09
	//ITSM 2.4 FS 13.7	
	var strText = "";
	for(var intNumberOfInsert=0;intNumberOfInsert<_swdoc.arrDiary.length;intNumberOfInsert++)
	{
		arrDetails = _swdoc.arrDiary[intNumberOfInsert]
		if(strText!="")strText += ","
		strText += arrDetails[0]+" has been modified from '"+arrDetails[1]+"' to '"+arrDetails[2]+"'";
	}
	if(strText!=""){
		var strParams = "contract="+pfu(_swdoc.oContract().pk_contract_id)+"&txt="+pfu(strText);
		app.g.submitsqs("form/tfcontract/add_diary_entry",strParams);
	}



	//-- ensure tab is active after saving
	if(!_swdoc.boolLoading)
		_swdoc.init_form();

}

						function setup_history()
{
	//RJC 05.01.09
	//ITSM 2.4 FS 13.7
	//Create array of insert details
	var arrInserts = new Array();

	//-- get matching contract record
	var oLiveContractRec = app.g.get_record("CONTRACT",contract.pk_contract_id);
	var liveVal="";
	//-- for each field check if diff from contract
	for (x=0; x < dd.tables['contract'].columns.length;x++)
	{

		var colName = dd.tables['contract'].columns[x].Name;

		if(colName=="contract_notes")continue;

		//make sure the field exists
		if(oLiveContractRec[colName]!=undefined)
		{
			//MessageBox(colName + " : " + FORM_VALUE + " : " + oLiveContractRec[colName]);
			//-- if values differ and value is not empty put into a field table
			//-- if numeric value and = 0 then ignore
			var FORM_VALUE = contract[colName];
			if((app.g.dd_isnumeric('contract',colName))&&(FORM_VALUE==0))continue;

			var	dispFormVal = FORM_VALUE;
			var liveVal = oLiveContractRec[colName];
			var dispLiveVal = liveVal;
			if(app.g.dd_isnumeric('contract',colName))
			{
				//-- F0099555
				if(liveVal<0)
					liveVal = app.g.fix_epoch(liveVal);
				if(FORM_VALUE<0)
					FORM_VALUE= app.g.fix_epoch(FORM_VALUE);

				dispFormVal = app.g.convert_epochddmmyyyy(FORM_VALUE);
				dispLiveVal = app.g.convert_epochddmmyyyy(liveVal);
			}

			if((liveVal!=FORM_VALUE))
			{
				arrInserts.push(new Array( app.g.dd_fieldlabel('contract',colName),app.g.pfs(dispLiveVal) , app.g.pfs(dispFormVal)))
			}
		}	
	}

	_swdoc.arrDiary = arrInserts;
}

						OnSaveData()
{
	// TODO: Add your event handler code here
	var boolIsValid = _swdoc.OnPreValidate();
	if(!boolIsValid)
		return false;

	if(_swdoc.boolInserting)
	{
		_swdoc.contractID = contract.pk_contract_id;

		var oRec = app.g.get_record("contract", contract.pk_contract_id);
		if(oRec)
		{
			MessageBox("There is already a record in the [contract] table with the ID of ["+contract.pk_contract_id+"]",16);
			return false;
		}


		var strParams = "name="+pfu(contract.pk_contract_id);
		var oRS = app.g.get_sqrecordset("form/tfcontract/get_priority",strParams);
		if(oRS.Fetch())
		{
			MessageBox("There is already a record in the [system_sla] table with the Name of ["+contract.pk_contract_id+"]",16);
			return false;	
		}



		try
		{
			contract.fk_priority = app.global.CreateSla(contract.pk_contract_id,contract.timezone,_swdoc.respTime, _swdoc.fixTime, false) ;
		}catch(err)
		{
			return false;
		}
		var boolSuccess = app.global.SetSla3rdPartyInfo(contract.fk_priority, contract.fk_company_id, contract.fk_manager, contract.owner_email, contract.owner_tel, contract.contract_notes,app.g.fix_epoch(contract.start_datex), app.g.fix_epoch(contract.end_datex), _swdoc.MF().flg_options.value); 
		if(!boolSuccess)
			return false;
		app.global.RefreshHelpdeskAnalystsTree();

	}
	else
	{
    	var strParams = "tz="+pfu(_swdoc.MF().tb_timezone.value)+"&rt="+pfu(_swdoc.respTime)+"&ft="+pfu(_swdoc.fixTime)+"&id="+pfu(contract.fk_priority);
		app.g.submitsqs("form/tfcontract/update_priority", strParams);
		var boolSuccess = app.global.SetSla3rdPartyInfo(contract.fk_priority, contract.fk_company_id, contract.fk_manager, contract.owner_email, contract.owner_tel, contract.contract_notes,app.g.fix_epoch(contract.start_datex), app.g.fix_epoch(contract.end_datex), _swdoc.MF().flg_options.value); 

	}_swdoc.setup_history();
	return true;
}

						OnPreValidate()
{
	// TODO: Add your event handler code here
	var respHrs = _swdoc.MF().resp_hrs.value;
	var respMins = _swdoc.MF().resp_mins.value;
	if(respHrs==0 && respMins==0)
	{
		MessageBox("You must specify a Response Time.",48);
		return VALIDATE_FAIL;
	}
	var fixHrs = _swdoc.MF().fix_hrs.value;
	var fixMins = _swdoc.MF().fix_mins.value;
	if(fixHrs==0 && fixMins==0)
	{
		MessageBox("You must specify a Fix Time",48);
		return VALIDATE_FAIL;
	}

	var localRespTime = parseInt(respHrs)*60+parseInt(respMins);
	var localFixTime = parseInt(fixHrs)*60+parseInt(fixMins);
	if(localRespTime < localFixTime)
	{
		_swdoc.respTime = localRespTime*60;
		_swdoc.fixTime = localFixTime*60;
		return VALIDATE_DEFAULT;
	}else
	{
		MessageBox("The fix time you have specified is less than or equal to the response time.\nPlease enter a fix time that is greater than the response time.",48);
		return VALIDATE_FAIL;
	}
	return VALIDATE_DEFAULT;
}

