						OnDataSaved()
{

    var pDoc = app.g.get_me_document("COMPANY",_swdoc.oSite().fk_company_id);
    if(pDoc)
    {
    	var intCurSel = pDoc.MF().sl_sites.curSel;
    	pDoc.MF().sl_sites.Refresh();
    	pDoc.MF().sl_sites.SetRowSelected(intCurSel,true);
    }

    //-- 
    //-- NWJ - show tab - and also update parent _swdoc
    if(_swdoc.boolInserting)
    {		

    	//-- create cmdb relationship
    	if(pDoc)
    	{
    		//-- need to reload site record to get fk_cmdb_id
    		_swdoc.LoadRecordData("swdata", "site", _swdoc.oSite().site_name);

    		//-- check if have default rel if so form silently ..
    		var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
   			app.cmdb.generate_meconfigrelation(pDoc.oCompany().fk_cmdb_id,_swdoc.oSite().fk_cmdb_id,strRel);  
    	}else
    	{
   		 	var parentDoc = app.g.get_me_document("BROWSE_SITES",_swdoc.oSite().fk_company_id);

    		if(parentDoc)
    		{
    			//-- need to reload site record to get fk_cmdb_id
    			_swdoc.LoadRecordData("swdata", "site", _swdoc.oSite().site_name);

    			//-- check if have default rel if so form silently ..
    			var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultOrgSiteRelationship");
   				app.cmdb.generate_meconfigrelation(parentDoc.oCompany().fk_cmdb_id,_swdoc.oSite().fk_cmdb_id,strRel);  
    		}

    	}

		_swdoc.MF().TabSiteInfo.ShowTabItem(2,true);    
		_swdoc.boolInserting=false;
	}

}

