							OnFormLoaded()
{
	//-- NWJ
	//-- 21.04.2005

	if (_swdoc.oSite().site_name == "")
	{
		app.g.popuplate_form_fromargs("site", _swdoc, true, true);
		TabSiteInfo.ShowTabItem(2,false);
		TabSiteInfo.ShowTabItem(3,false);		
		TabSiteInfo.ShowTabItem(4,false);		
		_etab(TabSiteInfo, 0);
		_evi(_swdoc.MF().mb_view1, false);
		_swdoc.boolInserting=true;
	}
	else
	{
		TabSiteInfo.ShowTabItem(2,true);
		TabSiteInfo.ShowTabItem(3,true);		
		_swdoc.boolInserting=false;
	}
}

							mbtn_contact_view_OnMenuItem(strName, nItemID)
{
	var iProperties = 1;

	if(nItemID == iProperties)
	{
		sl_contacts.EditRecord(sl_contacts.curSel);
		_swdoc.load_site_customers(sl_contacts.curSel);
	}


}

							mbtn_contact_actions_OnMenuItem(strName, nItemID)
{
	var iNewContact = 1;
	var iDeleteContact = 2;
	var iSearchAdd = 3;
	var iRemove = 4;
	var iSupport = 5;

	if(nItemID == iRemove)
	{
		var strKey = sl_contacts.GetItemTextRaw(sl_contacts.curSel, 'keysearch');
		var intCI = sl_contacts.GetItemTextRaw(sl_contacts.curSel, 'fk_cmdb_id');
		if(strKey=="")return;

		if(confirm("Are you sure you want to disassociate the selected customer from this site?"))
		{
			//-- delete ci relationship (always assume that ME is parent)
			if(_swdoc.oSite().fk_cmdb_id>0)
			{
				app.cmdb.delete_meconfigrelation(_swdoc.oSite().fk_cmdb_id, intCI);
			}   		

			//-- update userdb where site is this site and is selected customer
			var strParams = "cust="+pfu(strKey)+"&site="+pfu(_swdoc.oSite().site_name);
			app.g.submitsqs("site/remove_user_association",strParams);
		}

		_swdoc.load_site_customers();

		_swdoc.MF().sl_contacts.Refresh();
	}	
	else if(nItemID == iNewContact)
	{
		//-- NWJ
    	//-- Open new contact form
    	//-- pass in pk_company_auto_id so that we can link the two
    	var strURL = "fk_company_id=" + pfu(_swdoc.oSite().fk_company_id) + "&site=" + pfu(_swdoc.oSite().site_name);
		app.g.sl_openformforadd(sl_contacts,"TfCustomer", strURL, 1,null, function()
		{
		 	_swdoc.load_site_customers();
			_swdoc.MF().sl_contacts.Refresh();
		});
	}
	else if(nItemID == iDeleteContact)
	{

		//-- 29.07.2004
    	//-- NWJ
    	//-- Delete the selected contact
    	var selectedRow     = sl_contacts.curSel;
    	var strConfirm		= "Are you sure you want to delete this customer from the database ?";

		app.g.sl_deldbrow(sl_contacts,selectedRow,'keysearch',null,strConfirm);
		_swdoc.load_site_customers();
		_swdoc.MF().sl_contacts.Refresh();
	}
	else if (nItemID == iSearchAdd)
	{

		//-- show list of company customers, that are not currently linked to this site, that can link to this site
		var strURL = "fk_company_id=" + pfu(_swdoc.oSite().fk_company_id);
		app.g.search_for("customers", true,strURL,function(oData)
		{
			if (oData.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
			if(_swdoc.oSite().fk_cmdb_id>0)
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCustRelationship");
				//MessageBox(oForm.document.strSelectedCMDBIDs)
				app.cmdb.generate_meconfigrelation(_swdoc.oSite().fk_cmdb_id, oData.selectedcmdbids, strRel);
			}   		

   			//- ask user to confirm that site is the customers main site 
   			if(confirm("Is this site the main site for the selected customer(s)?"))
   			{
				//-- update  userdb
				var strParams = "users="+pfu(oData.selectedkeys)+"&site="+pfu(_swdoc.oSite().site_name);
				MessageBox(strParams);
				app.g.submitsqs("site/set_user_association",strParams);
			}	

			_swdoc.load_site_customers();
			_swdoc.MF().sl_contacts.Refresh();

			return;
		});
	}	
	else if (nItemID == iSupport)
	{

		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var strEmail = app.g.sl_getrawcolvalue(sl_contacts,-1,"email");
			app.g.start_remote_support(strEmail);
		}	
		else
		{
			MessageBox("Remote Support is not available for this customer");
		}
	}


}

							mbtn_company_ci_actions1_OnMenuItem(strName, nItemID)
{
	var iAssignCI = 1;
	var iRemoveCI = 2;	
	var iRemoteSupport = 3;

	var intCI = sl_results.GetItemTextRaw(sl_results.curSel, 'pk_auto_id');

	if(nItemID == iAssignCI)
	{
		//-- relate the CI to the Company
		var strURL = "fk_company_id=" + pfu(_swdoc.oSite().fk_company_id);
		app.cmdb.search_ci(true,strURL,function(sResults)
		{
			if (sResults.selectedkeys=="") return;

			//--
			//-- create ci relationship (always assume that customer always uses a CI)
			if(_swdoc.oSite().fk_cmdb_id>0)
			{
				var strRel = dd.GetGlobalParamAsString("Application Settings/CMDB/Types/DefaultSiteCIRelationship");
				app.cmdb.generate_meconfigrelation(_swdoc.oSite().fk_cmdb_id, sResults.selectedkeys, strRel,"No");
			}
			//- ask user to confirm that company is the owner of the CI's	
   			if(confirm("Is this site the owner of the selected configuration item(s)?"))
   			{
				//-- update the config items
				var strParams = "cikeys="+pfu(sResults.selectedkeys)+"&site="+pfu(_swdoc.oSite().site_name)+"&companyid="+pfu(_swdoc.oSite().fk_company_id)+"&companyname="+pfu(_swdoc.oSite().comp_name);
				app.g.submitsqs("site/set_ci_association",strParams);
			}	
			mainform.sl_results.Refresh();		
			return;	
		});
    }
	else if(nItemID == iRemoveCI)
	{
		//-- remove link between ci and company
		if (intCI > 0)
    	{   	
    		if (!confirm("Are you sure you want to unlink the selected configuration item from this site ?")) return;

    		//--
			//-- delete ci relationship (always assume that ME is parent)
			if(_swdoc.oSite().fk_cmdb_id>0)
			{
				app.cmdb.delete_meconfigrelation(_swdoc.oSite().fk_cmdb_id, intCI);
			}   		
    		mainform.sl_results.Refresh();	
    		//-- reset owner of ci if company is actual owner
    		var strParams = "site="+pfu(_swdoc.oSite().site_name)+"&cikeys="+pfu(intCI);
        	if (app.g.submitsqs("site/remove_ci_association",strParams))
        	{
        	}
    	}
    	mainform.sl_results.Refresh();	
	}
	else if(nItemID==iRemoteSupport)
	{
		//-- Remote Support Enabled?
		if(app.g.remote_support_enabled())
		{
			//-- Attempt to launch a pinned support session
			var intItem = app.g.sl_getrawcolvalue(sl_ci1,-1,"pk_auto_id");
			var oCI = app.g.get_record("config_itemi",intItem);
			app.g.start_pinned_remote_support(oCI);
		}	
		else
		{
			MessageBox("Remote Support is not available for this item");
		}
	}





}

							sl_ci1_OnItemSelected(nRow)
{
    //-- Load CI info based on CI loaded
    //-- testing Gerrys new dataloader
    var ci_id  = sl_ci1.GetItemTextRaw(nRow,"pk_auto_id");
    if (ci_id == "")
    {
        _swdoc.config_key     = 0;
        _swdoc.config_item    = "";       
        _een(mbtn_view_ci, false);
    }
    else
    {
    	var ci_item = sl_ci1.GetItemTextRaw(nRow,"ck_config_item");
        _swdoc.config_key     = ci_id;
        _swdoc.config_item    = ci_item;
		_een(mbtn_view_ci, true);        
    }

}

