							mbtn_view_ci_OnMenuItem(strName, nItemID)
{
  	var iProperties = 1;
	var iVCMParents = 2;	
	var iVCMChildren = 5;		
	var iInventory = 3;
	var iChangeSchedule = 4;

	//-- get intCI
  	var intCI = app.g.sl_getrawcolvalue(sl_results,-1,"pk_auto_id");
  	if(intCI>0)
  	{
  		//-- pass in text identfier as well
  		var strItem = app.g.sl_getrawcolvalue(sl_results,-1,"ck_config_item");

		if(nItemID == iProperties)
		{
			app.cmdb.popup_configitem(intCI,true,"",false,function()
			{
				return;
			});
    	}
		else if(nItemID == iVCMParents)
		{
			app.cmdb.view_vcm(intCI,true);
		}
		else if(nItemID == iVCMChildren)
		{
			app.cmdb.view_vcm(intCI,false);
		}
		else if(nItemID == iInventory)
		{
			app.cmdb.view_inventory(intCI,strItem);
		}
		else if(nItemID == iChangeSchedule)
		{
			app.cmdb.view_schedule(intCI,strItem);
		}
	}
}

							popup_address1_OnLeftClick()
{

	//-- NWJ - 22.08.2008
	//-- show address record
	if (_swdoc.oSite().fk_address_id > 0)
	{
		app.OpenFormForEdit("efAddress", _swdoc.oSite().fk_address_id, "", true, function(){
			app.cfg.load_address(_swdoc.MF(),_swdoc.oSite().fk_address_id,_swdoc.oSite());
			_swdoc.UpdateFormFromData();
			//-- if not isnerting save
			if(!_swdoc.boolInserting)_swdoc.Save(0);
			return true
		});
	}
	return true;
}

							tba6_OnDropDown()
{
	app.g.search_for("address",true,"",function(oRes)
	{
		if(oRes.selectedkeys=="")return;

		var arrKeys = oRes.selectedkeys.split(",");
		var arrName = oRes.selectedother.split(",");	
		var intAddressID = arrKeys[0];

		if ((intAddressID == '') || (intAddressID == 'none')) return;

		if (intAddressID != _swdoc.oSite().fk_address_id) 
		{
			_swdoc.oSite().fk_address_id = intAddressID;		

			//-- load address fields - will set company address_1 , 2 etc
			app.cfg.load_address(_swdoc.MF(),_swdoc.oSite().fk_address_id,_swdoc.oSite());
			_swdoc.UpdateFormFromData();	
		}
		return false;
	});
}

							tba6_OnRightClick()
{
	//-- clear address
	app.cfg.clear_address(_swdoc.oSite());
	_swdoc.UpdateFormFromData();


	return true;
}

