							btn_search1_OnPressed()
{

	_swdoc.strCIFilter = ""; 
	var intPageNo = 1;
	_swdoc.intCIPageNo = 1;
	var strFilter = ""

	if(fd_type.value != ""){
		_swdoc.strCIFilter += " AND fk_child_type LIKE '" + fd_type.value + "%'";
	}
	if(fd_id.value != ""){
		_swdoc.strCIFilter += " AND fk_child_itemtext LIKE '% " + fd_id.text + " %'";
	}
	if(fd_description.value != ""){
		_swdoc.strCIFilter +=" AND childdesc LIKE '% " + app.global.PrepareForSQL(fd_description.value) + " %'";
	}

	//--Update Document so Filter is Global
	strFilter = "&strFilter="+_swdoc.strCIFilter;

	//-- Add Paging
	strFilter += "&paged=true&start="+_swdoc.intCIPageNo+"&end=&cmdb_type='ME->SITE'&fk_cmdb_id="+_swdoc.oSite().fk_cmdb_id+"&webclient="+_swdoc.webclient+"";

	//-- apply filter
	app.g.sl_filter(sl_results,strFilter,null,0)
	_swdoc.MF().sl_results.Refresh();

	//Show Next Button
	if(sl_results.rowCount() < _swdoc.intPerPage)
	{
		_een(_swdoc.MF().btn_next,false);	
	}else
	{
		_een(_swdoc.MF().btn_next,true);	
	}
	_een(_swdoc.MF().btn_prev,false);

}

							btn_reset1_OnPressed()
{
	_ete(_swdoc.MF().fd_description, "");
	_ete(_swdoc.MF().fd_id, "");
	_ete(_swdoc.MF().fd_type,"");
	_een(_swdoc.MF().btn_next,false);
	_een(_swdoc.MF().btn_prev,false);	
	_swdoc.strCIFilter = "";
	_slf(_swdoc.MF().sl_results , "empty=1");
	_swdoc.MF().sl_results.Refresh();

}

